var textForPages =["\u8ab2\u7a0b\u300a\u89e3\u8aaa\u54e1\u547d\u300b\uff0e\u8ab2\u524d\u95b1\u8b80 \u000d\u000a\u8b1b\u5e2b\uff1a\u674e\u5049\u6587\u000d\u000a\uff11\uff0f\uff18 \u000d\u000a\u8352\u1c00\u81ea\u7136\u540d\u7684\u7531\u4f86\u53ca\u81ea\u7136\u89e3\u8aaa\u54e1\u8a13\u7df4\u7684\u6f14\u9032\u2015\u8352\u1c00\u8003\u53e4\u7cfb\u5217\u4e4b\u56db111.07.22 \u000d\u000a \u8352\u1c00\u7684\u5fd7\u5de5\u7d55\u5927\u90e8\u5206\u90fd\u6709\u500b\u81ea\u7136\u540d\uff0c\u5f88\u591a\u975e\u8352\u1c00\u4eba\u807d\u8457\u4f19\u4f34\u5f7c\u6b64\u4ee5\u52d5\u690d\u7269\u6216\u5404\u7a2e\u81ea\u7136\u73fe\u000d\u000a\u8c61\u4f86\u4e92\u7a31\uff0c\u89ba\u5f97\u975e\u5e38\u6709\u8da3\uff0c\u800c\u8352\u1c00\u4f19\u4f34\u4e5f\u6703\u597d\u5947\uff0c\u7a76\u7adf\u8981\u6c42\u8352\u1c00\u5fd7\u5de5\u53d6\u81ea\u7136\u540d\u7684\u7531\u4f86\u662f\u4ec0\u000d\u000a\u9ebc?\u8352\u1c00\u88e1\u4e5f\u6709\u4e00\u4e9b\u7565\u6709\u51fa\u5165\u7684\u7248\u672c\u5728\u6d41\u50b3\u8457\u3002 \u000d\u000a\u6700\u8fd1\u56e0\u70ba\u64d4\u4efb\u4e86\u4e8c\u5341\u591a\u5e74\u76e3\u4e8b\u7684\u8001\u4f19\u6881\u535a\u6dde\u8001\u5e2b\u53c8\u1bff\u63d0\u9019\u500b\u8a71\u984c\uff0c\u65bc\u662f\u6211\u82b1\u4e86\u4e8c\u5929\uff0c\u9664\u4e86\u8a62\u000d\u000a\u554f\u5341\u591a\u4f4d\u65e9\u671f\u8352\u1c00\u89e3\u8aaa\u54e1\u4f5c\u53e3\u8ff0\u6b77\u53f2(\u5f9e\u7b2c\u4e00\u671f\u5230\u7b2c\u4e94\u671f)\uff0c\u4e5f\u7ffb\u67e5\u4e86\u8352\u1c00\u5f9e\u7c4c\u5099\u5230\u6210\u7acb\u5f8c\u982d\u000d\u000a\u4e94\u5e74\u6240\u51fa\u7248\u7684\u6240\u6709\u520a\u7269\uff0c\u5927\u6982\u62fc\u6e4a\u51fa\u4e00\u500b\u8f03\u70ba\u5b8c\u6574\u7684\u8108\u7d61\u3002 \u000d\u000a\u9019\u500b\u6982\u5ff5\u6700\u65e9\u8d77\u6e90\u53ef\u4ee5\u8ffd\u6eaf\u4e8c\u672c\u66f8\u8ddf\u4e00\u90e8\u96fb\u5f71\u3002 \u000d\u000a\u4e00\u672c\u662f\u5f90\u4ec1\u4fee\u8001\u5e2b\u6240\u5beb\u7684\u300c\u8d64\u9053\u7121\u98a8\u300d\uff0c\u53e6\u4e00\u672c\u662f\u63cf\u8ff0\u7f8e\u6d32\u5370\u5730\u5b89\u539f\u4f4f\u6c11\u751f\u6d3b\u7684\u300c\u5c11\u5e74\u5c0f\u6a39\u000d\u000a\u4e4b\u6b4c\u300d\uff0c\u4e00\u90e8\u96fb\u5f71\u662f\u7f8e\u570b\u5f71\u661f\u51f1\u6587\u67ef\u65af\u7d0d\u4e3b\u6f14\u7684\u300c\u8207\u72fc\u5171\u821e\u300d\u3002 \u000d\u000a \u300c\u8207\u72fc\u5171\u821e\u300d\u63cf\u8ff0\u4e00\u4f4d\u7f8e\u570b\u5357\u5317\u6230\u722d\u53d7\u50b7\u5f8c\uff0c\u88ab\u6d3e\u99d0\u5230\u897f\u90e8\u908a\u7586\u99d0\u5b88\u7684\u8ecd\u5b98\uff0c\u8207\u539f\u4f4f\u6c11\u000d\u000a\u4ea4\u4e0a\u670b\u53cb\u6700\u5f8c\u4e26\u8207\u4ed6\u5011\u4e26\u80a9\u5c0d\u6297\u767d\u4eba\u7684\u4fb5\u7565\uff0c\u8207\u72fc\u5171\u821e\u5c31\u662f\u539f\u4f4f\u6c11\u7d66\u51f1\u6587\u67ef\u65af\u7d0d\u53d6\u7684\u540d\u5b57\uff0c\u000d\u000a\u56e0\u70ba\u539f\u4f4f\u6c11\u66fe\u89c0\u5bdf\u5230\u51f1\u6587\u8207\u72fc\u5728\u8ffd\u9010\u5b09\u6232\u7684\u6a23\u5b50\uff0c\u5f88\u50cf\u8ddf\u72fc\u5728\u8df3\u821e\u3002 \u000d\u000a \u96fb\u5f71\u5728\u7576\u5e74\u975e\u5e38\u8ce3\u5ea7\uff0c\u4e0a\u6620\u6642\u9593\u662f\u8352\u1c00\u6210\u7acb\u524d\u4e0d\u4e45\uff0c\u6240\u4ee5\u5e7e\u4e4e\u6bcf\u500b\u65e9\u671f\u7684\u4f19\u4f34\u5168\u90fd\u770b\u000d\u000a\u904e\uff0c\u4e5f\u56e0\u6b64\u77e5\u9053\u539f\u4f4f\u6c11\u53d6\u540d\u5b57\u6703\u547c\u61c9\u90a3\u500b\u4eba\u8ddf\u81ea\u7136\u751f\u547d\u4e92\u52d5\u7684\u56e0\u7de3\u3002 \u000d\u000a \u8352\u1c00\u57281995\u5e746\u6708\u6210\u7acb\uff0c\u4e00\u6210\u7acb\u5c31\u63a5\u9023\u8fa6\u4e86\u5e7e\u5834\u5c0d\u793e\u6703\u5927\u773e\u958b\u653e\u7684\u81ea\u7136\u9ad4\u9a57\u6d3b\u52d5\u53ca\u5927\u578b\u7684\u000d\u000a\u7cfb\u5217\u6f14\u8b1b\uff0c\u5fd9\u5230\u5341\u6708\u624d\u958b\u59cb\u6e96\u5099\u958b\u8fa6\u7b2c\u4e00\u671f\u7684\u81ea\u7136\u89e3\u8aaa\u54e1\u8a13\u7df4\u3002 \u000d\u000a \u5728\u540c\u4e00\u500b\u6642\u5019\uff0c\u4e5f\u5728\u5354\u6703\u5730\u4e0b\u5ba4\u7684\u5c0f\u7a7a\u9593\u958b\u59cb\u8209\u8fa6\u300c\u5468\u56db\u8352\u1c00\u898b\u300d\u7684\u5c0f\u578b\u6f14\u8b1b\u6216\u5206\u4eab\u000d\u000a\u6703\uff0c\u9019\u500b\u6f14\u8b1b\u6c92\u6709\u7de8\u5217\u8b1b\u5e2b\u8cbb\uff0c\u539f\u5247\u4e0a\u662f\u9080\u8acb\u6709\u5c08\u9577\u7684\u5fd7\u5de5\u4f86\u5206\u4eab\uff0c\u9019\u500b\u50b3\u7d71\u6301\u7e8c\u4e86\u4e8c\u5341\u4e03\u000d\u000a\u5e74\uff0c\u76f4\u5230\u73fe\u5728\u3002 \u000d\u000a \u7576\u6642\u5c31\u4ee5\u9019\u9ebc\u9ad8\u7684\u983b\u7387\u8209\u8fa6\u56fa\u5b9a\u7684\u6f14\u8b1b\uff0c\u6709\u5e7e\u500b\u7528\u610f\uff0c\u4e00\u500b\u662f\u5275\u9020\u767c\u8868\u7684\u821e\u53f0\uff0c\u8b93\u8352\u1c00\u000d\u000a\u5fd7\u5de5\u6709\u7df4\u7fd2\u8207\u6210\u9577\u7684\u6a5f\u6703\uff0c\u53e6\u4e00\u500b\u662f\u5e0c\u671b\u5fd7\u5de5\u4e0d\u662f\u5230\u5354\u6703\u53ea\u662f\u4f86\u958b\u6703\uff0c\u800c\u662f\u807d\u5b8c\u4f19\u4f34\u5206\u4eab ","(\u53ef\u4ee5\u52a0\u6df1\u8a8d\u8b58\u4f19\u4f34)\uff0c\u5728\u6f14\u8b1b\u5f8c\u7559\u4e0b\u4f86\u7e7c\u7e8c\u958b\u6703\u3002\u9078\u5468\u56db\u4e5f\u662f\u6709\u7528\u610f\u7684\uff0c\u56e0\u70ba\u82e5\u662f\u5047\u65e5\u8981\u8fa6\u000d\u000a\u6d3b\u52d5\uff0c\u5fd7\u5de5\u5468\u56db\u665a\u4e0a\u4f86\u958b\u6703\uff0c\u505a\u500b\u6700\u5f8c\u78ba\u8a8d\u6216\u6e96\u5099\u5668\u6750\uff0c\u6642\u9593\u525b\u525b\u597d\uff0c\u800c\u4e14\u5468\u56db\u665a\u4e0a\u7684\u4ea4\u901a\u000d\u000a\u6bd4\u8f03\u4e0d\u6703\u585e\u8eca\u3002 \u000d\u000a \u8352\u1c00\u6210\u7acb\u982d\u4e8c\u5e74\u7684\u9031\u56db\u8352\u1c00\u898b\u7684\u8b1b\u5e2b\uff0c\u5927\u6982\u90fd\u662f\u7531\u6211\u9080\u8acb\uff0c\u6240\u4ee5\u6211\u4e5f\u90fd\u6703\u5168\u7a0b\u53c3\u52a0\uff0c\u5176\u000d\u000a\u4e2d\u6bcf\u500b\u6708\u6211\u6703\u5b89\u6392\u4e00\u5834\u8b80\u66f8\u8a0e\u8ad6\u6703\uff0c\u63a8\u85a6\u4e00\u4e9b\u597d\u66f8\uff0c\u8b93\u4f19\u4f34\u5171\u8b80\u3002 \u000d\u000a 1996\u5e741\u6708\u8a0e\u8ad6\u5f90\u4ec1\u4fee\u8001\u5e2b\u5beb\u7684\u300c\u8d64\u9053\u7121\u98a8\u300d\uff0c\u7531\u7406\u4e8b\u5305\u5ff5\u6f84\u8001\u5e2b\u4e3b\u6301\uff1b1996\u5e742\u6708\u8a0e\u8ad6\u000d\u000a\u300c\u5c11\u5e74\u5c0f\u6a39\u4e4b\u6b4c\u300d\uff0c\u5f90\u4ec1\u4fee\u8001\u5e2b\u4e3b\u6301\u3002\u8a18\u5f97\u5728\u9019\u5169\u5834\u8a0e\u8ad6\u4e2d\uff0c\u90fd\u6709\u63d0\u5230\u539f\u4f4f\u6c11\u7528\u81ea\u7136\u4f86\u547d\u540d\u000d\u000a\u7684\u7fd2\u6163\uff0c\u4e0d\u53ea\u662f\u300c\u5c11\u5e74\u5c0f\u6a39\u4e4b\u6b4c\u300d\u88e1\u7684\u4e3b\u89d2\u5c0f\u6a39\u547c\u61c9\u4e86\u8207\u72fc\u5171\u821e\uff0c\u5f90\u8001\u5e2b\u4e5f\u4ee5\u4ed6\u7684\u7d93\u9a57\u4f86\u5206\u000d\u000a\u4eab\uff0c\u56e0\u70ba\u4ed6\u66fe\u7d93\u53c3\u8207\u53f0\u7063\u7684\u8fb2\u8015\u968a\u5230\u83f2\u5f8b\u8cd3\u3001\u5c3c\u52a0\u62c9\u74dc\u3001\u7b49\u5730 (\u8fb2\u8015\u968a\u662f\u53f0\u7063\u7576\u5e74\u5f88\u1bff\u8981\u7684\u000d\u000a\u5916\u4ea4\u65b9\u5f0f)\uff0c\u8a8d\u8b58\u4e86\u5f88\u591a\u4e0d\u540c\u570b\u5bb6\u7684\u539f\u4f4f\u6c11\uff0c\u77e5\u9053\u4ed6\u5011\u4e5f\u90fd\u6709\u7528\u81ea\u7136\u4f86\u547d\u540d\u7684\u7fd2\u6163\u3002 \u000d\u000a \u539f\u4f4f\u6c11\u81f3\u4eca\u4ecd\u80fd\u8ddf\u5927\u81ea\u7136\u4fdd\u6301\u826f\u597d\u7684\u4e92\u52d5\uff0c\u8352\u1c00\u63a2\u7d22\u4eba\u8ddf\u81ea\u7136\u5982\u4f55\u4fdd\u6301\u5e73\u8861\uff0c\u539f\u4f4f\u6c11\u7684\u000d\u000a\u53e4\u8001\u667a\u6167\u662f\u5f88\u503c\u5f97\u6211\u5011\u5b78\u7fd2\u7684\uff0c\u56e0\u6b64\uff0c\u5f90\u8001\u5e2b\u4e5f\u958b\u59cb\u9f13\u52f5\u4f19\u4f34\u5011\u5f9e\u70ba\u81ea\u5df1\u53d6\u4e00\u500b\u81ea\u7136\u540d\u958b\u000d\u000a\u59cb\u3002 \u000d\u000a \u4f46\u662f\u9f13\u52f5\u6b78\u9f13\u52f5\uff0c\u982d\u5e7e\u5e74\u4f19\u4f34\u771f\u7684\u70ba\u81ea\u5df1\u53d6\u81ea\u7136\u540d\u7684\u4eba\u4e26\u4e0d\u591a\uff0c\u56e0\u70ba\u64da\u64d4\u4efb\u8352\u1c00\u7b2c\u4e00\u5c46\u000d\u000a\u7406\u4e8b\uff0c\u8ca0\u8cac\u8352\u1c00\u81ea\u7136\u89e3\u8aaa\u54e1\u8a13\u7df4\u7684\u5305\u5ff5\u6f84\u8001\u5e2b\u56de\u61b6\uff0c\u76f4\u5230\u59791998\u5e749\u6708\u96e2\u958b\u53f0\u7063\u5230\u862d\u5dbc\u4efb\u6559\u000d\u000a\u4e4b\u524d\uff0c\u5979\u81ea\u5df1\u4e5f\u90fd\u9084\u6c92\u6709\u53d6\u81ea\u7136\u540d\u3002 \u000d\u000a \u4e0d\u904e\u5230\u4e861999\u5e74\u4e0a\u534a\u5e74\u5df2\u7d50\u8a13\u7684\u7b2c\u56db\u671f\u89e3\u8aaa\u54e1\u5df2\u7d93\u6709\u4e0d\u5c11\u4eba\u6709\u81ea\u7136\u540d\u4e86\uff0c\u64da\u56db\u671f\u7684\u9673\u7d05\u000d\u000a\u96f2\u6559\u5b98\u56de\u61b6\uff0c\u7576\u6642\u5979\u5011\u9084\u66fe\u7d93\u5728\u5354\u6703\u7684\u5730\u4e0b\u5ba4\u5206\u4eab\u8207\u4ecb\u7d39\u81ea\u5df1\u7684\u81ea\u7136\u540d\u3002 \u000d\u000a \u4e0d\u904e\u771f\u6b63\u7684\u95dc\u9375\u56e0\u7d20\u662f\u5f9e\u7b2c\u4e94\u671f\u89e3\u8aaa\u54e1\u8a13\u7df4\u958b\u59cb\uff0c\u5c07\u70ba\u81ea\u5df1\u53d6\u81ea\u7136\u540d\u5217\u70ba\u4f5c\u696d\u3002\u8352\u1c00\u5fd7\u000d\u000a\u5de5\u8a13\u7df4\u7684\u86fb\u8b8a\u4e5f\u662f\u5f9e\u81ea\u7136\u89e3\u8aaa\u54e1\u8a13\u7df4\u7b2c\u4e94\u671f\u53ca\u7b2c\u516d\u671f\u958b\u59cb\u3002 \u000d\u000a \u8a71\u8aaa\u8352\u1c00\u7c4c\u5099\u8207\u8349\u5275\u7684\u90a3\u6279\u5fd7\u5de5\u771f\u7684\u975e\u5e38\u5fd9\uff0c\u9664\u4e86\u8981\u81ea\u6211\u5b78\u7fd2\uff0c\u5354\u52a9\u8352\u1c00\u7684\u884c\u653f\u6703\u52d9\u8207\u000d\u000a\u7406\u5ff5\u63a8\u5ee3\uff0c\u53c3\u8207\u4e26\u8209\u8fa6\u5404\u7a2e\u6d3b\u52d5\uff0c\u5f9e\u898f\u5283\u8a0e\u8ad6\uff0c\u63a2\u52d8\u5230\u57f7\u884c\uff0c\u4eba\u4eba\u8eab\u517c\u591a\u8077\uff0c\u56e0\u6b64\u982d\u56db\u5e74\u8fa6\u000d\u000a\u7684\u89e3\u8aaa\u54e1\u8a13\u7df4\u4e5f\u53ea\u80fd\u5982\u540c\u7576\u6642\u53f0\u7063\u84ec\u52c3\u767c\u5c55\u7684\u81ea\u7136\u89e3\u8aaa\u8ab2\u7a0b\u4e00\u822c(\u8a31\u591a\u793e\u5718\u8207\u570b\u5bb6\u516c\u5712\u90fd\u6709\u000d\u000a\u8fa6)\uff0c\u898f\u5283\u5ba4\u5167\u8ab2\u3001\u6236\u5916\u8ab2\uff0c\u7136\u5f8c\u9080\u8acb\u8b1b\u5e2b\uff0c\u4e0a\u8ab2\u6642\u8ca0\u8cac\u9ede\u540d\uff0c\u6700\u5f8c\u8a08\u7b97\u51fa\u7f3a\u5e2d\u6642\u6578\u662f\u5426\u7b26\u000d\u000a\u5408\u7d50\u8a13\u6a19\u6e96\u3002 \u000d\u000a \u4f46\u662f\u8fa6\u4e86\u56db\u671f\u5f8c\u6211\u5011\u767c\u73fe\uff0c\u53c3\u52a0\u7684\u5b78\u54e1\u9664\u975e\u7279\u5225\u71b1\u5fc3\uff0c\u6216\u8005\u8207\u7b2c\u4e00\u6279\u5fd7\u5de5\u7279\u5225\u6295\u7de3\u6216\u6709\u000d\u000a\u7279\u6b8a\u6df5\u6e90\uff0c\u4e0d\u7136\u5f80\u5f80\u53ea\u662f\u4f86\u4e0a\u8ab2\uff0c\u5b78\u54e1\u7684\u4e92\u52d5\u53ca\u8207\u8352\u1c00\u8001\u4f19\u4f34\u4e4b\u9593\u7684\u9023\u7d50\u4e26\u4e0d\u5920\uff0c\u56e0\u6b64\u5f88\u5bb9\u000d\u000a\u6613\u6d41\u5931\uff0c\u6c92\u6709\u8db3\u5920\u7684\u62c9\u529b\u8b93\u4ed6\u5011\u6301\u7e8c\u7559\u5728\u8352\u1c00\u8b8a\u6210\u9577\u671f\u7684\u5fd7\u5de5\u3002 "," \u8a18\u5f97\u6211\u66fe\u8207\u8001\u5e79\u90e8\u958b\u4e86\u597d\u5e7e\u6b21\u6703\uff0c\u7b2c\u4e94\u671f\u958b\u59cb\u5f15\u5165\u5927\u1c01\u7684\u5c0f\u7d44\u8f14\u5c0e\u54e1\u5236\u5ea6\uff0c\u4e26\u4e14\u958b\u59cb\u6709\u000d\u000a\u4e86\u4f5c\u696d\uff0c\u4e0d\u7ba1\u662f\u5ba4\u5167\u8ab2\u6216\u6236\u5916\u8ab2\uff0c\u90fd\u6703\u9810\u7559\u5c0f\u7d44\u6642\u9593\uff0c\u8b93\u5c0f\u7d44\u8f14\u5c0e\u54e1\u5e36\u8457\u5c0f\u7d44\u6210\u54e1\u4e00\u8d77\u8a0e\u000d\u000a\u8ad6\uff0c\u9664\u4e86\u500b\u4eba\u4f5c\u696d\u5916\uff0c\u5076\u723e\u9084\u6709\u5c0f\u7d44\u8981\u5171\u540c\u5b8c\u6210\u7684\u4f5c\u696d\u3002\u9019\u9ebc\u4e00\u4f86\uff0c\u53c3\u52a0\u8a13\u7df4\u7684\u65b0\u4f19\u4f34\u4e00\u5b9a\u000d\u000a\u80fd\u8a8d\u8b58\u5e7e\u4f4d\u64d4\u4efb\u8f14\u5c0e\u54e1\u7684\u8001\u4f19\u4f34\uff0c\u5927\u5bb6\u90fd\u5728\u5b8c\u6210\u5171\u540c\u4f5c\u696d\u6642\u5f7c\u6b64\u4e5f\u80fd\u66f4\u719f\u6089\uff0c\u9023\u7d50\u6027\u6108\u5f37\uff0c\u000d\u000a\u5c0d\u8352\u1c00\u7684\u5411\u5fc3\u529b\u5c31\u6108\u5f37\uff0c\u7559\u4e0b\u4f86\u6301\u7e8c\u53c3\u8207\u8352\u1c00\u7576\u5fd7\u5de5\u7684\u6bd4\u4f8b\u5c31\u66f4\u9ad8\u4e86\u3002 \u000d\u000a \u81ea\u7136\u540d\u5c31\u662f\u5728\u7b2c\u4e94\u671f\u958b\u59cb\u6709\u898f\u5b9a\u4f5c\u696d\u6642\u5c0e\u5165\uff0c\u5f9e\u6b64\uff0c\u6bcf\u500b\u8352\u1c00\u7684\u89e3\u8aaa\u54e1\u4e00\u5b9a\u6709\u81ea\u7136\u540d\uff0c\u000d\u000a\u56e0\u70ba\u6c92\u5b8c\u6210\u9019\u500b\u4f5c\u696d\u5c31\u6c92\u6709\u8fa6\u6cd5\u7d50\u8a13\u53d7\u8b49\u3002\u7b2c\u4e94\u671f\u7684\u8a13\u7df4\u6210\u6548\u975e\u5e38\u597d\uff0c\u53ef\u4ee5\u8aaa\u662f\u8352\u1c00\u5fd7\u5de5\u8a13\u000d\u000a\u7df4\u86fb\u8b8a\u7684\u958b\u59cb\uff0c\u5230\u4e86\u7b2c\u516d\u671f\u624d\u5b8c\u6210\u6574\u500b\u904e\u7a0b\uff0c\u5176\u4e2d\u8981\u6b78\u529f\u65bc\u4e09\u500b\u4eba\u2500\u2500\u6c88\u632f\u4e2d(\u81ea\u7136\u540d\u662f\u773e\u000d\u000a\u6240\u7686\u77e5\u7684\u8001\u9df9)\u53ca\u8a31\u4e2d\u5149\u8207\u8521\u6708\u7f8e\u3002 \u000d\u000a \u8521\u6708\u7f8e\u662f\u6211\u7576\u5e74\u5728\u7ae5\u8ecd22\u5718\u8ddf\u6211\u4e00\u8d77\u64d4\u4efb\u5718\u9577\u7684\u4f19\u4f34\uff0c\u8a31\u4e2d\u5149\u662f\u5fc3\u7406\u8f14\u5c0e\u8207\u8aee\u5546\u5c08\u5bb6\uff0c\u000d\u000a\u6c88\u632f\u4e2d\u662f\u57fa\u9686\u9ce5\u6703\u7684\u5275\u8fa6\u4eba\uff0c\u96d6\u7136\u4ed6\u4e00\u751f\u8077\u5fd7\u662f\u5b88\u8b77\u8001\u9df9\uff0c\u4f46\u662f\u4ed6\u5c0d\u81ea\u6211\u89ba\u5bdf\uff0c\u6df1\u5c64\u751f\u614b\u000d\u000a\u5b78\uff0c\u4eba\u5728\u81ea\u7136\u4e2d\u7684\u4fee\u884c\u90fd\u9817\u6709\u5fc3\u5f97\uff0c\u56e0\u6b64\u7b2c\u516d\u671f\u89e3\u8aaa\u54e1\u7684\u8ab2\u7a0b\u5c31\u958b\u59cb\u878d\u5165\u9019\u4e9b\u300c\u5167\u89c0\u300d\u7684\u5fc3\u000d\u000a\u9748\u5c64\u6b21\u53ca\u5fc3\u7406\u8aee\u5546\u7684\u6280\u5de7\uff0c\u5f9e\u904e\u5f80\u4ee5\u5916\u5728\u77e5\u8b58\u70ba\u4e3b\u7684\u8ab2\u7a0b\u5b89\u6392\uff0c\u591a\u4e86\u81ea\u6211\u89ba\u5bdf\u8207\u7642\u7652\u7684\u7cbe\u000d\u000a\u795e\u3002 \u000d\u000a \u7b2c\u516d\u671f\u89e3\u8aaa\u54e1\u8a13\u7df4\u6b63\u5728\u98a8\u98a8\u706b\u706b\u5730\u9032\u884c\u6642\uff0c\u8352\u1c00\u89aa\u5b50\u5718\u4e5f\u6b63\u5728\u7c4c\u5099\u4e2d (\u7576\u6642\u53ea\u6709\u898f\u5283\u70ab\u8702\u000d\u000a\u9019\u500b\u968e\u6bb5)\uff0c\u89aa\u5b50\u5718\u5275\u8fa6\u4eba\u6797\u8000\u570b\u5e38\u52d9\u7406\u4e8b\uff0c\u7279\u5225\u5546\u8acb\u8001\u9df9\u4e5f\u4f86\u5354\u52a9\u89aa\u5b50\u5718\u5c0e\u5f15\u54e1\u7684\u8ab2\u7a0b\u898f\u000d\u000a\u5283\uff0c\u8001\u9df9\u5728\u90a3\u5e7e\u5e74\u4e5f\u82b1\u4e86\u975e\u5e38\u591a\u7684\u6642\u9593\u5f15\u5c0e\u8457\u4f19\u4f34\u5411\u5927\u81ea\u7136\u5b78\u7fd2\uff0c\u4e5f\u5960\u5b9a\u4e0b\u4eca\u5929\u8352\u1c00\u4fdd\u8b77\u5354\u000d\u000a\u6703\u975e\u5e38\u6709\u7279\u8272\u7684\u5fd7\u5de5\u8a13\u7df4\u7684\u57fa\u790e\u3002 \u000d\u000a \u8352\u1c00\u9019\u4e8c\u5341\u591a\u5e74\u7684\u6210\u9577\uff0c\u6709\u975e\u5e38\u591a\u7684\u4f19\u4f34\u6295\u5165\uff0c\u8b1d\u8b1d\u5927\u5bb6\u7684\u5949\u737b\uff0c\u8352\u1c00\u4e5f\u56e0\u532f\u805a\u6bcf\u500b\u4eba\u000d\u000a\u7684\u5fc3\u529b\u8207\u6c57\u6c34\uff0c\u800c\u66f4\u52a0\u8301\u58ef\u4e14\u8c50\u5bcc\u3002 \u000d\u000a\u70ba\u81ea\u5df1\u53d6\u4e00\u500b\u81ea\u7136\u540d...\u5beb\u65bc2016\u5e74 \u000d\u000a \u8352\u1c00\u4fdd\u8b77\u5354\u6703\u7684\u5fd7\u5de5\u6bcf\u500b\u4eba\u90fd\u5fc5\u9808\u4ee5\u5927\u81ea\u7136\u7684\u751f\u7269\u6216\u73fe\u8c61\uff0c\u4f86\u70ba\u81ea\u5df1\u547d\u540d\uff0c\u5fd7\u5de5\u5011\u5f7c\u6b64\u000d\u000a\u5c31\u4ee5\u81ea\u7136\u540d\u4e92\u7a31\uff0c\u5f80\u5f80\u5927\u4f19\u4e0a\u5c71\u4e0b\u6d77\u7c4c\u5099\u6d3b\u52d5\u591a\u5e74\uff0c\u719f\u5f97\u4e0d\u5f97\u4e86\uff0c\u537b\u4e0d\u77e5\u9053\u5c0d\u65b9\u8eab\u5206\u8b49\u4e0a\u7684\u000d\u000a\u540d\u5b57\u3002 \u000d\u000a \u9019\u4e9b\u5e74\u56e0\u70ba\u624b\u6a5f\u7684line\u901a\u8a0a\u6d41\u884c\uff0c\u56e0\u70ba\u6bcf\u500b\u4eba\u53ea\u6709\u4e00\u500b\u5e33\u6236\u540d\uff0c\u8352\u1c00\u7684\u5fd7\u5de5\u70ba\u4e86\u8b93\u4f19\u4f34\u5011\u000d\u000a\u77e5\u9053\u662f\u8ab0\uff0c\u5f80\u5f80\u6703\u5728\u5e33\u6236\u540d\u7a31\u6a19\u8a3b\u81ea\u5df1\u7684\u81ea\u7136\u540d\u3002\u56e0\u6b64\u6211\u61c9\u9080\u5230\u5404\u5730\u6f14\u8b1b\u6642\u8ddf\u4e3b\u8fa6\u4eba\u804a\u5929","\u6642\uff0c\u5e38\u5e38\u9047\u5230\u4ed6\u5011\u63d0\u8d77\u8ab0\u8ab0\u8ab0\u662f\u8352\u1c00\u5fd7\u5de5\uff0c\u4ed6\u5011\u4e4b\u6240\u4ee5\u77e5\u9053\uff0c\u662f\u56e0\u70ba\u597d\u5947\u70ba\u4f55line\u7684\u540d\u5b57\u5f88\u000d\u000a\u5947\u7279\uff0c\u8a62\u554f\u4e0b\u624d\u77e5\u9053\uff0c\u539f\u4f86\u8352\u1c00\u6709\u9019\u9ebc\u4e00\u500b\u50b3\u7d71\u3002 \u000d\u000a \u9019\u500b\u50b3\u7d71\u5f9e\u8352\u1c00\u4e00\u6210\u7acb\u5c31\u958b\u59cb\u8981\u6c42\uff0c\u9996\u5148\u662f\u7576\u4f5c\u5fd7\u5de5\u8a13\u7df4\u88e1\u7684\u4e00\u9805\u4f5c\u696d\uff0c\u8981\u53bb\u627e\u51fa\u4e00\u7a2e\u000d\u000a\u81ea\u7136\u754c\u7684\u751f\u7269\u8ddf\u81ea\u5df1\u5167\u5728\u7684\u672c\u6027\u76f8\u5408\uff0c\u6216\u662f\u81ea\u5df1\u559c\u6b61\u6216\u8207\u81ea\u5df1\u6709\u7de3\u5206\u7684\uff0c\u900f\u904e\u9019\u9805\u4f5c\u696d\uff0c\u4e00\u000d\u000a\u65b9\u9762\u4e5f\u662f\u85c9\u6b64\u8b93\u6bcf\u500b\u4eba\u6709\u6a5f\u6703\u627e\u5230\u81ea\u5df1\u8207\u81ea\u7136\u7684\u9023\u7d50\u9ede\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u9019\u4e5f\u662f\u5f88\u591a\u539f\u4f4f\u6c11\u90e8\u843d\u000d\u000a\u7684\u547d\u540d\u7fd2\u6163\uff0c\u53ea\u662f\u5728\u6587\u660e\u6f14\u9032\u904e\u7a0b\uff0c\u4eba\u7684\u751f\u6d3b\u8207\u81ea\u7136\u751f\u547d\u6108\u4f86\u6108\u758f\u9060\uff0c\u540d\u5b57\u4e5f\u9010\u6f38\u812b\u96e2\u9019\u4e9b\u000d\u000a\u8207\u65e5\u5e38\u751f\u6d3b\u5bc6\u5207\u76f8\u95dc\u7684\u4e8b\u7269\u3002 \u000d\u000a \u547d\u540d\u9019\u4ef6\u4e8b\uff0c\u81ea\u53e4\u4ee5\u4f86\u5c31\u662f\u5f88\u614e\u1bff\u7684\uff0c\u9664\u4e86\u5225\u4eba\u4e0d\u65b7\u4ee5\u6b64\u4f86\u7a31\u547c\u4f60\uff0c\u540d\u5b57\u6240\u4ee3\u8868\u7684\u610f\u8c61\u000d\u000a\u4e5f\u6703\u5f71\u97ff\u5225\u4eba\u5c0d\u4f60\u7684\u8a8d\u77e5\uff0c\u518d\u52a0\u4e0a\u9577\u671f\u4e0d\u65b7\u5730\u81ea\u5df1\u66f8\u5beb\uff0c\u5225\u4eba\u53eb\u4f60\uff0c\u9019\u662f\u7a2e\u50ac\u7720\uff0c\u5c0d\u6f5b\u610f\u8b58\u000d\u000a\u662f\u6709\u5f88\u5927\u5f71\u97ff\u7684\u3002 \u000d\u000a \u96d6\u7136\u838e\u58eb\u6bd4\u4e9e\u7684\u6232\u5287\u88e1\u6709\u500b\u5c0d\u767d:\u300c\u5373\u4fbf\u73ab\u7470\u4e0d\u53eb\u505a\u73ab\u7470\uff0c\u5b83\u9084\u662f\u4e00\u6a23\u7684\u82ac\u82b3!\u300d\u9019\u662f\u52f5\u5fd7\u000d\u000a\u7684\u8a71\u8a9e\uff0c\u52f5\u5fd7\u7684\u610f\u601d\u901a\u5e38\u662f\u4e00\u7a2e\u671f\u5f85\uff0c\u800c\u4e0d\u662f\u4e8b\u5be6\uff0c\u56e0\u70ba\u4e00\u500b\u5b57\u7684\u5b57\u578b\u6216\u610f\u7fa9\uff0c\u771f\u7684\u6703\u5f71\u97ff\u000d\u000a\u6211\u5011\u5c0d\u5b83\u7684\u611f\u89ba\u3002 \u000d\u000a \u5728\u5e36\u9818\u6c11\u773e\u505a\u81ea\u7136\u89c0\u5bdf\u6642\uff0c\u770b\u5230\u4efb\u4f55\u6771\u897f\uff0c\u5e7e\u4e4e\u6240\u6709\u4eba\u7b2c\u4e00\u500b\u554f\u984c\uff0c\u751a\u81f3\u552f\u4e00\u7684\u554f\u984c\uff0c\u000d\u000a\u5c31\u662f\u554f:\u300c\u5b83\u53eb\u4ec0\u9ebc\u540d\u5b57?\u300d\u96d6\u7136\u6211\u5011\u5728\u89e3\u8aaa\u6642\uff0c\u5e38\u6703\u63d0\u9192\u6c11\u773e\u5b83\u53eb\u300c\u5f35\u4e09\u674e\u56db\u738b\u4e94\u300d\u90fd\u4e0d\u1bff\u000d\u000a\u8981\uff0c\u6211\u5011\u53ea\u8981\u4ed4\u7d30\u89c0\u5bdf\u5b83\u9577\u5f97\u5982\u4f55?\u5b83\u9577\u5728\u4ec0\u9ebc\u5730\u65b9?\u5b83\u8207\u5468\u908a\u751f\u7269\u8207\u74b0\u5883\u7684\u95dc\u4fc2\u5982\u4f55?\u6211\u5011\u000d\u000a\u7684\u63d0\u9192\u662f\u5e0c\u671b\u5927\u5bb6\u8981\u4e86\u89e3\u5b83\u7684\u672c\u8cea\uff0c\u4e0d\u8981\u53ea\u60f3\u77e5\u9053\u540d\u5b57\uff0c\u5c31\u50cf\u53bb\u8003\u8a66\u60f3\u8ffd\u6c42\u6a19\u6e96\u7b54\u6848\uff0c\u7576\u6211\u000d\u000a\u5011\u4ee5\u70ba\u77e5\u9053\u7b54\u6848\uff0c\u6240\u6709\u7684\u5b78\u7fd2\u5c31\u6703\u505c\u6b62\u3002 \u000d\u000a \u5176\u5be6\u6211\u5011\u9019\u6a23\u7684\u8aaa\u6cd5\u662f\u6709\u554f\u984c\u7684\uff0c\u96d6\u7136\u4e0d\u80fd\u8aaa\u662f\u932f\u7684\uff0c\u4f46\u4e0d\u7ba1\u9019\u6771\u897f\u53eb\u4ec0\u9ebc\u540d\u5b57\uff0c\u662f\u9055\u000d\u000a\u53cd\u5927\u8166\u8a8d\u77e5\u5b78\u7fd2\u7684\u6b65\u9a5f\u8207\u7d50\u69cb\u3002 \u000d\u000a \u6211\u5011\u5728\u5b78\u7fd2\u4efb\u4f55\u65b0\u7684\u4e8b\u7269\u6216\u6982\u5ff5\u6642\uff0c\u4e00\u5b9a\u8981\u5c07\u9019\u500b\u65b0\u6771\u897f\u627e\u5230\u8ddf\u6211\u5011\u539f\u5148\u5df2\u719f\u6089\u4e86\u89e3\u7684\u000d\u000a\u4e8b\u7269\u9023\u7d50\u9ede\uff0c\u63db\u53e5\u8a71\u8aaa\uff0c\u6211\u5011\u5fc5\u9808\u5c07\u5b83\u5206\u985e\u64fa\u5728\u67d0\u500b\u65e2\u6709\u7684\u77e5\u8b58\u67b6\u69cb\u4e2d\uff0c\u5b83\u624d\u80fd\u88ab\u8a18\u61b6\uff0c\u800c\u000d\u000a\u540d\u5b57\uff0c\u4e5f\u5c31\u662f\u7d66\u4ed6\u4e00\u500b\u6a19\u7c64\uff0c\u4e00\u500b\u5206\u985e\uff0c\u6c92\u6709\u9019\u500b\u8d77\u59cb\u9ede\uff0c\u6240\u6709\u7684\u6771\u897f\u662f\u9032\u4e0d\u5230\u5927\u8166\u88e1\u53bb\u000d\u000a\u7684\uff0c\u5c31\u7b97\u8a18\u4e86\u4e00\u5207\uff0c\u5c11\u4e86\u540d\u5b57\uff0c\u4ee5\u5f8c\u4e5f\u7121\u6cd5\u63d0\u53d6\uff0c\u5c31\u50cf\u8cc7\u6599\u5b58\u6a94\u4e00\u5b9a\u8981\u7d66\u500b\u6a94\u540d\uff0c\u6c92\u6709\u6a94\u000d\u000a\u540d\uff0c\u4ee5\u5f8c\u662f\u627e\u4e0d\u5230\u7684\u3002 \u000d\u000a \u800c\u4e14\u81ea\u53e4\u4ee5\u4f86\uff0c\u5728\u4eba\u985e\u7684\u96c6\u9ad4\u6f5b\u610f\u8b58\u4e2d\uff0c\u540d\u5b57\u4ee3\u8868\u4e00\u500b\u4eba\u7684\u672c\u8cea\uff0c\u56e0\u6b64\u81ea\u53e4\u4ee5\u4f86\u6709\u6240\u8b02\u000d\u000a\u300c\u540d\u8af1\u300d\uff0c\u4e0d\u80fd\u76f4\u547c\u4eba\u7684\u540d\uff0c\u6975\u7aef\u4e00\u9ede\u7684\u8a71\uff0c\u4e2d\u570b\u6b77\u4ee3\u5e1d\u738b\u53d6\u4e86\u4ec0\u9ebc\u540d\u5b57\uff0c\u5168\u570b\u7684\u8001\u767e\u59d3\u5f9e","\u6b64\u5c31\u4e0d\u80fd\u518d\u66f8\u5beb\u6216\u4f7f\u7528\u9019\u500b\u5b57\uff0c\u641e\u5230\u5f8c\u4f86\uff0c\u7687\u5e1d\u70ba\u4e86\u907f\u514d\u64fe\u6c11\uff0c\u751a\u81f3\u9020\u6210\u6587\u5316\u50b3\u905e\u7684\u969c\u7919\uff0c\u000d\u000a\u6240\u4ee5\u53d6\u7684\u540d\u5b57\u90fd\u975e\u5e38\u51b7\u50fb\uff0c\u751a\u81f3\u81ea\u5275\u6587\u5b57\uff0c\u78ba\u4fdd\u4e0d\u6703\u9020\u6210\u5927\u5bb6\u751f\u6d3b\u4e0a\u7684\u4e0d\u4fbf\u3002 \u000d\u000a \u5728\u53e4\u4ee3\u5c0f\u8aaa\u6216\u50b3\u8aaa\u6545\u4e8b\u4e2d\uff0c\u4e5f\u4e0d\u65b7\u51fa\u73fe\u5982\u6b64\u60c5\u7bc0\uff0c\u6211\u5011\u4ee5\u5916\u865f\u4ee5\u5225\u540d\u884c\u8d70\u65bc\u4e16\u9593\uff0c\u6975\u529b\u000d\u000a\u96b1\u779e\u81ea\u5df1\u7684\u771f\u540d\uff0c\u53ea\u6709\u5c0d\u81ea\u5df1\u5b8c\u5168\u4fe1\u8cf4\u7684\u4eba\u624d\u6703\u900f\u9732\uff0c\u56e0\u70ba\u77e5\u9053\u4e00\u500b\u4eba\u7684\u540d\u5b57\u5c31\u53ef\u4ee5\u64cd\u63a7\u000d\u000a\u4ed6\u3002 \u000d\u000a \u53e4\u4ee3\u5deb\u91ab\u6216\u73fe\u4eca\u9053\u58eb\u4f5c\u6cd5\uff0c\u7e3d\u6703\u756b\u7b26\uff0c\u8a8d\u70ba\u7b26\u662f\u5177\u6709\u795e\u8056\u7684\u529b\u1c01\uff0c\u6216\u662f\u53ec\u559a\u795e\u9748\u6216\u662f\u93ae\u000d\u000a\u58d3\u9b3c\u602a\u3002\u756b\u7b26\uff0c\u5230\u5e95\u5728\u756b\u4ec0\u9ebc?\u5176\u5be6\u5c31\u662f\u4ee5\u53e4\u4ee3\u7684\u5b57\u9ad4\u66f8\u5beb\u9b3c\u795e\u7684\u771f\u540d\uff0c\u56e0\u70ba\u77e5\u9053\u9b3c\u795e\u7684\u771f\u000d\u000a\u540d\uff0c\u5c31\u53ef\u4ee5\u77e5\u9053\u5b83\u5011\u7684\u672c\u8cea\uff0c\u5c31\u53ef\u4ee5\u64cd\u63a7\u5b83\u5011\u3002 \u000d\u000a \u662f\u7684\uff0c\u5deb\u5e2b\u7d42\u5176\u4e00\u751f\u5c31\u662f\u5728\u5c0b\u627e\u4e8b\u7269\u7684\u540d\u5b57\uff0c\u8ddf\u81ea\u7136\u89c0\u5bdf\u5bb6\u4e00\u6a23\u3002 ","\uff12\uff0f\uff18 \u000d\u000a\u767e\u5e74\u7d44\u7e54\u7684\u4eba\u624d\u8207\u5fd7\u5de5\u767c\u5c55\u7b56\u7565\u2015\u9858\u666f\u57fa\u91d1\u6703\u4f86\u8a2a\u7cfb\u5217\u4e4b\u4e8c111.07.06 \u000d\u000a \u4e0a\u6587\u63d0\u5230\u4e0d\u8fd1\u4eba\u60c5\u7684\u8981\u5fd7\u5de5\u51fa\u9322\u53c8\u51fa\u529b\uff0c\u9ad8\u8209\u6975\u7aef\u7406\u60f3\u6027\u7684\u7121\u79c1\u5949\u737b\uff0c\u80cc\u5f8c\u8457\u773c\u7684\u662f\u5e0c\u000d\u000a\u671b\u8352\u1c00\u4fdd\u8b77\u5354\u6703\u662f\u500b\u64c1\u6709\u516c\u4fe1\u529b\u6709\u5f71\u97ff\u529b\u7684\u767e\u5e74\u7d44\u7e54\u3002 \u000d\u000a \u4e16\u754c\u7684\u904b\u4f5c\u662f\u4e00\u5834\u7121\u9650\u8cfd\u5c40(\u8acb\u770b\u53e6\u5916\u5c08\u6587\u6558\u8ff0)\uff0c\u56e0\u6b64\u6211\u5e0c\u671b\u53f0\u7063\u80fd\u64c1\u6709\u5b88\u8b77\u74b0\u5883\u70ba\u5b97\u65e8\u000d\u000a\u7684\u767e\u5e74\u7d44\u7e54\uff0c\u8b93\u4e00\u4ee3\u53c8\u4e00\u4ee3\u7684\u5fd7\u5de5\u6240\u52aa\u529b\u7d2f\u7a4d\u51fa\u7684\u793e\u6703\u6b63\u9762\u5f71\u97ff\u529b\uff0c\u53ef\u4ee5\u6301\u7e8c\u7559\u7d66\u5f8c\u4ee3\u5b50\u000d\u000a\u5b6b\u3002 \u000d\u000a \u767e\u5e74\u7d44\u7e54\u6700\u1bff\u8981\u7684\u57fa\u790e\u662f\u8b93\u4eba\u4fe1\u4efb\uff0c\u56e0\u6b64\u9664\u4e86\u6703\u52d9\u8207\u6c7a\u7b56\u516c\u958b\u900f\u660e\u7121\u79c1\u5fc3\u4e4b\u5916\uff0c\u53c3\u8207\u5fd7\u000d\u000a\u5de5\u7684\u7d20\u8cea\u975e\u5e38\u1bff\u8981\uff0c\u53ef\u662f\u8981\u8b93\u5c0d\u7684\u4eba\u53ef\u4ee5\u5728\u7d44\u7e54\u88e1\u767c\u5149\u767c\u71b1\uff0c\u5c31\u5f97\u5efa\u7acb\u826f\u597d\u7684\u6587\u5316\u3002 \u000d\u000a \u4e00\u822c\u516c\u76ca\u5718\u9ad4\u7684\u904b\u4f5c\u5927\u591a\u63a1\u53d6\u4ee5\u300c\u6709\u9322\u7684\u51fa\u9322\uff0c\u6709\u529b\u7684\u51fa\u529b\u300d\u70ba\u539f\u5247\uff0c\u56e0\u6b64\u6703\u6709\u4eba\u5c08\u8cac\u000d\u000a\u52df\u6b3e\u6216\u9f13\u52f5\u793e\u6703\u5927\u773e\u6350\u6b3e\uff0c\u7136\u5f8c\u518d\u62db\u52df\u5fd7\u5de5\u4f86\u57f7\u884c\uff0c\u5fd7\u5de5\u51fa\u529b\uff0c\u96d6\u7136\u4e0d\u6703\u62ff\u916c\u52de\uff0c\u4f46\u662f\u51fa\u4efb\u000d\u000a\u52d9\u904e\u7a0b\u6240\u884d\u751f\u7684\u652f\u51fa\uff0c\u6bd4\u5982\u4ea4\u901a\u8cbb\u3001\u4fbf\u7576\u9322\uff0c\u6216\u8005\u6d3b\u52d5\u904e\u7a0b\u4e2d\u7684\u4f4f\u5bbf\u8cbb\u2026\u2026\u7531\u7d44\u7e54\u7de8\u9810\u7b97\u652f\u000d\u000a\u61c9\uff0c\u4f3c\u4e4e\u4e5f\u5f88\u5408\u7406\uff0c\u7121\u53ef\u539a\u975e\u3002 \u000d\u000a \u70ba\u4ec0\u9ebc\u8352\u1c00\u4e0d\u8ddf\u5176\u4ed6\u6240\u6709\u5718\u9ad4\u4e00\u6a23\uff0c\u300c\u5584\u5f85\u300d\u5fd7\u5de5\uff0c\u7686\u5927\u6b61\u559c?\u539f\u56e0\u6709\u5f88\u591a\uff0c\u59d1\u4e14\u8209\u4e00\u3001\u000d\u000a\u5169\u500b\u4f8b\u5b50\u7c21\u55ae\u8aaa\u660e\u3002 \u000d\u000a \u8352\u1c00\u5f9e\u6210\u7acb\u4ee5\u4f86\uff0c\u5c31\u5e0c\u671b\u6709\u5927\u1c01\u5fd7\u5de5\uff0c\u5e0c\u671b\u5275\u9020\u6700\u5927\u53ef\u80fd\u6027\u8b93\u793e\u6703\u5927\u773e\u4eba\u4eba\u90fd\u6709\u6a5f\u6703\u633a\u000d\u000a\u8eab\u800c\u51fa\u70ba\u74b0\u5883\u505a\u4e00\u9ede\u4e8b\u60c5\uff0c\u56e0\u70ba\u6211\u5011\u77e5\u9053\uff0c\u53ea\u6709\u771f\u6b63\u884c\u52d5\u904e\uff0c\u64d4\u4efb\u904e\u5fd7\u5de5\u7684\u4eba\uff0c\u624d\u6703\u771f\u6b63\u7684\u000d\u000a\u6539\u8b8a\uff0c\u4e0d\u6703\u53ea\u662f\u53e3\u982d\u8aaa\u652f\u6301\uff0c\u800c\u662f\u80fd\u6539\u8b8a\u4ed6\u7684\u50f9\u503c\u89c0\u8207\u751f\u6d3b\u7fd2\u6163\u3002 \u000d\u000a \u9019\u4e5f\u662f\u70ba\u4ec0\u9ebc\u8352\u1c00\u9019\u9ebc\u1bff\u8996\u5fd7\u5de5\u8a13\u7df4\u7684\u539f\u56e0\u3002\u6211\u5728\u8207\u9858\u666f\u5de5\u7a0b\u57fa\u91d1\u6703\u7684\u8a2a\u8ac7\u4e2d\uff0c\u4e5f\u66fe\u8209\u000d\u000a\u4f8b\u8aaa\uff0c\u5f9e\u8352\u1c00\u5275\u7acb\u5f8c\u6211\u8ca0\u8cac\u8352\u1c00\u6703\u52d9\u7684\u982d\u5341\u4e8c\u5e74\u4e2d\uff0c\u7d93\u5e38\u8ddf\u5404\u7fa4\u7d44\u53ec\u96c6\u4eba\u8207\u5206\u6703\u9577\u8aaa\uff0c\u4f60\u5011\u000d\u000a\u8fa6\u4e86\u591a\u76db\u5927\u7684\u6d3b\u52d5\uff0c\u6709\u6210\u5343\u4e0a\u842c\u7684\u6c11\u773e\u53c3\u52a0\uff0c\u90e8\u9577\u6216\u7e23\u9577\u6703\u51fa\u5e2d\u2026\u2026\u6211\u4e0d\u4e00\u5b9a\u6709\u7a7a\u53bb\uff0c\u4f46\u662f\u000d\u000a\u53ea\u8981\u4f60\u5011\u8fa6\u5fd7\u5de5\u8a13\u7df4\uff0c\u5373\u4f7f\u53ea\u6709\u5341\u4e94\u500b\u4eba\u5831\u540d\uff0c\u6211\u4e5f\u4e00\u5b9a\u5230\u3002 \u000d\u000a \u540c\u6642\u6211\u4e5f\u77e5\u9053\u6bcf\u500b\u6c11\u773e\u95dc\u5fc3\u7684\u8b70\u984c\u4ee5\u53ca\u8208\u8da3\u3001\u80fd\u529b\u8207\u6642\u9593\u90fd\u4e0d\u540c\uff0c\u56e0\u6b64\u5c31\u8981\u5275\u9020\u5404\u5f0f\u5404\u000d\u000a\u6a23\u7684\u821e\u53f0\uff0c\u8b93\u4e0d\u540c\u5c6c\u6027\u7684\u6c11\u773e\u90fd\u53ef\u4ee5\u4f9d\u81ea\u5df1\u7684\u7bc0\u594f\u64d4\u4efb\u5fd7\u5de5\uff0c\u9019\u4e5f\u6b63\u5982\u8907\u96dc\u4e14\u74b0\u74b0\u76f8\u6263\u7684\u793e\u000d\u000a\u6703\u4e00\u6a23\uff0c\u5b88\u8b77\u74b0\u5883\u5fc5\u9808\u5f9e\u793e\u6703\u773e\u591a\u9818\u57df\u540c\u6642\u52aa\u529b\uff0c\u6240\u8b02\u5206\u9032\u5408\u64ca\u3002 \u000d\u000a \u4f46\u662f\uff0c\u60f3\u50cf\u4e00\u4e0b\uff0c\u5982\u540c\u8352\u1c00\u6709\u90a3\u9ebc\u591a\u5de5\u4f5c\u59d4\u54e1\u6703\uff0c\u4ee5\u53ca\u5c1a\u672a\u6210\u7acb\u59d4\u54e1\u6703\u7684\u5de5\u4f5c\u5c0f\u7d44\u6216\u96e3\u4ee5\u000d\u000a\u8a08\u6578\u7684\u5c08\u6848\u8a08\u756b\uff0c\u4e5f\u5c31\u662f\u540c\u4e00\u6642\u9593\u5168\u53f0\u7063\u4e0d\u540c\u7684\u5730\u65b9\u90fd\u6709\u8a31\u591a\u4eba\u4ee5\u8352\u1c00\u4e4b\u540d\u5728\u884c\u52d5\u8457:\u6709\u4eba","\u5728\u70ba\u6d3b\u52d5\u63a2\u52d8\u5834\u5730\uff0c\u6709\u4eba\u6b63\u5728\u958b\u6703\u6e96\u5099\u516c\u807d\u6703\u7684\u8aaa\u5e16\uff0c\u6709\u4eba\u5728\u4f5c\u7269\u7a2e\u8abf\u67e5\uff0c\u6709\u4eba\u5728\u6fd5\u5730\u6e05\u9664\u000d\u000a\u5916\u4f86\u7a2e\uff0c\u6709\u4eba\u5728\u793e\u5340\u88e1\u966a\u4f34\u5f31\u52e2\u5b69\u5b50\u2026\u2026\u9019\u4e9b\u90fd\u662f\u8352\u1c00\u7684\u5fd7\u5de5\uff0c\u82e5\u8981\u652f\u61c9\u4fbf\u7576\u9322\u8ddf\u4ea4\u901a\u8cbb\uff0c\u000d\u000a\u8a72\u7528\u4ec0\u9ebc\u6a19\u6e96?\u82e5\u4eba\u4eba\u6709\u734e\u53c8\u6703\u662f\u96e3\u4ee5\u60f3\u50cf\u7684\u9f90\u5927\u958b\u92b7\u8207\u7121\u5e95\u6d1e\u3002\u8001\u7956\u5b97\u6709\u4e91:\u4e0d\u60a3\u5be1\u800c\u60a3\u4e0d\u000d\u000a\u5747\uff0c\u53ea\u8981\u6709\u4eba\u6709\uff0c\u6709\u4eba\u6c92\u6709\uff0c\u5206\u914d\u4e0d\u516c\u5e73\uff0c\u5c31\u6703\u51fa\u554f\u984c\u3002\u76f8\u53cd\u7684\uff0c\u53ea\u8981\u662f\u5fd7\u5de5\u8eab\u5206\uff0c\u4e0d\u7ba1\u505a\u000d\u000a\u751a\u9ebc\u4e8b\u90fd\u6c92\u6709\u4efb\u4f55\u9322\u53ef\u4ee5\u7533\u8acb\uff0c\u53cd\u800c\u53ef\u4ee5\u7559\u4e0b\u5fc3\u5fd7\u6700\u7d14\u7cb9\u7684\u597d\u4eba\u624d\u3002 \u000d\u000a \u518d\u8209\u500b\u5c0d\u65bc\u6d3b\u52d5\u57f7\u884c\u7d30\u7bc0\u7684\u5c0f\u4f8b\u5b50\u4f86\u8aaa\uff0c\u5047\u5982\u4e00\u5834\u6d3b\u52d5\u7de8\u4e86\u5fd7\u5de5\u7684\u8aa4\u9910\u8cbb\u8207\u4ea4\u901a\u8cbb\uff0c\u90a3\u000d\u000a\u5c31\u6703\u9650\u65bc\u7d93\u8cbb\u800c\u898f\u5b9a\u53ea\u80fd\u51fa\u5e7e\u500b\u5fd7\u5de5\uff0c\u6240\u4ee5\u8ca0\u8cac\u7684\u4f19\u4f34\u5c31\u88ab\u8feb\u8981\u53bb\u300c\u6311\u9078\u300d\u5fd7\u5de5\uff0c\u9019\u5176\u4e2d\u96e3\u000d\u000a\u514d\u6703\u589e\u6dfb\u4e0d\u5fc5\u8981\u7684\u7d1b\u64fe\uff0c\u53cd\u4e4b\uff0c\u4e0d\u7ba1\u8cc7\u6df1\u5fd7\u5de5\u6216\u8cc7\u6dfa\u5fd7\u5de5\u90fd\u4e00\u6a23\uff0c\u4eba\u4eba\u6c92\u9322\uff0c\u6240\u4ee5\u4f86\u5e7e\u500b\u4eba\u000d\u000a\u5e6b\u5fd9\u90fd\u6c92\u95dc\u4fc2\uff0c\u6c92\u6709\u5fd7\u5de5\u4eba\u6578\u7684\u9650\u5236\uff0c\u591a\u591a\u76ca\u5584\uff0c\u56e0\u6b64\u4efb\u4f55\u6d3b\u52d5\u4e5f\u90fd\u6709\u6a5f\u6703\u6210\u70ba\u5fd7\u5de5\u5011\u4e92\u76f8\u000d\u000a\u89c0\u6469\u5b78\u7fd2\uff0c\u751a\u81f3\u76f8\u805a\u806f\u8abc\u7684\u6eab\u99a8\u6642\u5149\u3002 \u000d\u000a \u540c\u6642\uff0c\u56e0\u70ba\u8352\u1c00\u5fd7\u5de5\u6c92\u6709\u4efb\u4f55\u300c\u798f\u5229\u300d\uff0c\u751a\u81f3\u5728\u7d44\u7e54\u6587\u5316\u4e2d\uff0c\u64d4\u4efb\u5fd7\u5de5\u5e79\u90e8\u4e5f\u6c92\u6709\u4efb\u4f55\u000d\u000a\u300c\u6b0a\u529b\u300d\uff0c\u5728\u9019\u9ebc\u300c\u82db\u523b\u300d\u74b0\u5883\u4e0b\u9084\u9858\u610f\u6301\u7e8c\u4ed8\u51fa\uff0c\u80fd\u7559\u4e0b\u4f86\u7684\u5fd7\u5de5\u4e00\u5b9a\u662f\u71b1\u60c5\u6709\u4f7f\u547d\u611f\u7684\u000d\u000a\u4eba\u3002\u800c\u4e14\u8352\u1c00\u662f\u7531\u4e0b\u800c\u4e0a\u81ea\u7136\u7522\u751f\u5e79\u90e8\uff0c\u56e0\u6b64\u5fd7\u5de5\u80fd\u64d4\u4efb\u5e79\u90e8\u4e00\u5b9a\u662f\u9577\u4e45\u4ee5\u4f86\u4ed6\u4ed8\u51fa\u6700\u591a\uff0c\u000d\u000a\u773e\u671b\u6240\u6b78\uff0c\u540c\u6642\u4e5f\u56e0\u70ba\u53c8\u51fa\u9322\u53c8\u51fa\u529b\u53c8\u6c92\u6709\u4efb\u4f55\u300c\u597d\u8655\u300d\uff0c\u6240\u4ee5\u4e5f\u4e0d\u6703\u6709\u4eba\u6436\u4f4d\u5b50\u4f54\u8457\u4f4d\u5b50\u000d\u000a\u4e0d\u653e\uff0c\u56e0\u6b64\u8352\u1c00\u6210\u7acb\u8fd1\u4e09\u5341\u5e74\u4f86\uff0c\u4efb\u4f55\u8077\u52d9\u6539\u9078\u6642\uff0c\u5f9e\u4f86\u4e5f\u4e0d\u6703\u51fa\u73fe\u4ec0\u9ebc\u300c\u9ed1\u51fd\u300d\u3002 \u000d\u000a \u4f46\u662f\u5fd7\u5de5\u53c8\u51fa\u9322\u53c8\u51fa\u529b\uff0c\u5c0d\u65bc\u8352\u1c00\u9019\u7a2e\u5168\u570b\u6027\u7d44\u7e54\uff0c\u5176\u5be6\u4e5f\u6709\u7f3a\u9ede\u7684\u3002 \u000d\u000a \u56e0\u70ba\u5404\u500b\u7fa4\u7d44\u90fd\u6709\u81ea\u5df1\u7684\u5fd7\u5de5\u8a13\u7df4\u5167\u5bb9\uff0c\u800c\u4e14\u5e79\u90e8\u5fc5\u9808\u5230\u5404\u7e23\u5e02\u5206\u6703\u8b1b\u8ab2\uff0c\u5357\u5317\u5954\u6ce2\u5fc5\u000d\u000a\u9808\u8ca0\u64d4\u7684\u4ea4\u901a\u8cbb\u5c31\u5f88\u5687\u4eba\uff0c\u6bd4\u5982\u6709\u4e00\u6b21\u8207\u89aa\u5b50\u6559\u80b2\u59d4\u54e1\u6703\u524d\u4efb\u53ec\u96c6\u4eba\u5dd2\u5927\u5c71\u4f19\u4f34\u804a\u5929\uff0c\u7576\u6642\u000d\u000a\u662f\u5341\u6708\u4efd\uff0c\u4ed6\u8aaa\u7576\u5e74\u5ea6(\u4e00\u81f3\u5341\u6708)\u4ed6\u5230\u5404\u5730\u89aa\u5b50\u5718\u8fa6\u7684\u5404\u7d1a\u8a13\u7df4\u4e0a\u8ab2\u6240\u8cb7\u7684\u9ad8\u9435\u7968\uff0c\u6709\u7d93\u6709\u000d\u000a\u516b\u842c\u591a\u5143\u4e86\u3002 \u000d\u000a \u6240\u4ee5\u7d93\u6fdf\u6c92\u6709\u90a3\u9ebc\u5bec\u88d5\u7684\u4f19\u4f34\uff0c\u82e5\u64d4\u4efb\u8352\u1c00\u5927\u7fa4\u7d44\u7684\u5e79\u90e8\uff0c\u7684\u78ba\u5c31\u6703\u6709\u9ede\u5403\u529b\u3002\u6211\u5011\u4e86\u000d\u000a\u89e3\u9019\u500b\u9650\u5236\uff0c\u4f46\u662f\u4e5f\u4e0d\u80fd\u70ba\u4e86\u5c11\u6578\u500b\u6848\u800c\u7834\u58de\u300c\u51fa\u9322\u53c8\u51fa\u529b\u300d\u7684\u6587\u5316\uff0c\u5e78\u597d\uff0c\u53ea\u8981\u4e0d\u64d4\u4efb\u5927\u000d\u000a\u7fa4\u7d44\u5e79\u90e8\uff0c\u53ea\u5728\u5404\u5730\u7e23\u5e02\u64d4\u4efb\u5fd7\u5de5\u5c31\u6c92\u6709\u4ea4\u901a\u8cbb\u8207\u4f4f\u5bbf\u8cbb\u7684\u8ca0\u64d4\uff0c\u9084\u662f\u53ef\u4ee5\u70ba\u74b0\u5883\u8ca2\u737b\u4e00\u5df1\u000d\u000a\u4e4b\u529b\u3002 \u000d\u000a \u5fd7\u5de5\u51fa\u529b\u53c8\u51fa\u9322\u9084\u6709\u4e00\u500b\u597d\u8655\uff0c\u5c31\u662f\u8352\u1c00\u53ea\u8981\u5c08\u5fc3\u5730\u8fa6\u5fd7\u5de5\u8a13\u7df4\u300c\u52df\u4eba\u300d\u5c31\u597d\u4e86\uff0c\u56e0\u70ba\u000d\u000a\u53ea\u8981\u6709\u4eba\uff0c\u6211\u5011\u5c31\u80fd\u505a\u4e8b\u3002\u8352\u1c00\u5fd7\u5de5\u4e0d\u6703\u4e5f\u4e0d\u9700\u8981\u7b49\u300c\u4e0a\u9762\u300d\u6279\u51c6\u7136\u5f8c\u64a5\u7d93\u8cbb\u624d\u958b\u59cb\u505a\u4e8b\uff0c\u000d\u000a\u56e0\u70ba\u6211\u5011\u5df2\u7d93\u7fd2\u6163\u5728\u6c92\u6709\u4efb\u4f55\u8cc7\u6e90\u4e0b\u505a\u4e8b\uff0c\u9019\u4e5f\u662f\u6211\u5011\u5e38\u5e38\u63d0\u5230\u7684\uff0c\u53ea\u8981\u4f60\u767c\u5fc3\u9858\u70ba\u53f0\u7063\u74b0\u000d\u000a\u5883\u505a\u9ede\u4e8b\uff0c\u8352\u1c00\u88e1\u6709\u8a31\u591a\u5fd7\u5de5\uff0c\u5c31\u80fd\u966a\u4f34\u8457\u4f60\u4e00\u8d77\u5b8c\u6210\u5922\u60f3\u3002 ","\uff13\uff0f\uff18 \u000d\u000a\u767e\u5e74\u7d44\u7e54\u57fa\u696d\u9577\u9752\u2015\u7121\u9650\u8cfd\u5c40\u7cfb\u5217\u4e4b4 \u000d\u000a \u8352\u1c00\u4fdd\u8b77\u5354\u6703\u524d\u5929\u9078\u5b8c\u7b2c\u5341\u5c46\u7684\u6703\u54e1\u4ee3\u8868\uff0c\u4e0b\u500b\u6708\u5e95\u53ec\u958b\u7684\u6703\u54e1\u4ee3\u8868\u5927\u6703\u5c07\u9078\u51fa\u7b2c\u5341\u5c46\u000d\u000a\u7684\u7406\u76e3\u4e8b\uff0c\u9019\u5c46\u7684\u5e79\u90e8\u4e5f\u5c07\u5e36\u9818\u8352\u1c00\u9081\u5165\u4e09\u5341\u5468\u5e74\u3002 \u000d\u000a \u5f9e\u8352\u1c00\u5275\u7acb\u6642\u7684\u7b2c\u4e00\u6279\u5e79\u90e8\u8d77\uff0c\u6211\u5011\u6709\u8a08\u756b\u7684\u5728\u6bcf\u5c46\u6539\u9078\u6642\u90fd\u6703\u5f15\u9032\u4e9b\u65b0\u4f19\u4f34\uff0c\u9019\u9ebc\u4e00\u000d\u000a\u5c46\u4e00\u5c46\u7684\u8b93\u51fa\u821e\u53f0\uff0c\u5230\u4e86\u7b2c\u5341\u5c46\u7e3d\u7b97\u7576\u5e74\u5275\u6703\u53ca\u7b2c\u4e00\u5e74\u5c31\u52a0\u5165\u7684\u5fd7\u5de5\u5e79\u90e8\u5c07\u6703\u5168\u90e8\u9000\u51fa\u5e38\u52d9\u000d\u000a\u7406\u4e8b\u6703\uff0c\u76e4\u9ede\u4e0b\u4e00\u5c46\u53ef\u80fd\u63a5\u624b\u7684\u5e79\u90e8\uff0c\u5927\u6982\u90fd\u662f\u4e94\u5341\u6b72\u4e0a\u4e0b\uff0c\u8352\u1c00\u5fd7\u5de5\u8cc7\u6b77\u5341\u4e94\u5e74\u5de6\u53f3\u7684\u4f19\u000d\u000a\u4f34\u3002 \u000d\u000a \u4e5f\u56e0\u70ba\u7b97\u662f\u6574\u500b\u4e16\u4ee3\u7684\u4ea4\u66ff\uff0c\u6240\u4ee5\u6211\u5728\u9019\u4e09\u5e74\u8352\u1c00\u8209\u8fa6\u7684\u5404\u7fa4\u7d44\u5fd7\u5de5\u5e79\u90e8\u8a13\u7df4\u6642\uff0c\u7e3d\u662f\u000d\u000a\u6703\u7279\u5225\u5f37\u8abf\u7121\u9650\u8cfd\u5c40\u7684\u6982\u5ff5\u8207\u5efa\u69cb\u767e\u5e74\u7d44\u7e54\u7684\u671f\u5f85\u3002 \u000d\u000a \u56e0\u70ba\u770b\u904e\u592a\u591a\u7684\u7d44\u7e54\uff0c\u4e0d\u7ba1\u662f\u4f01\u696d\u6216\u6c11\u9593\u5718\u9ad4\uff0c\u7576\u51fa\u73fe\u67d0\u500b\u5091\u51fa\u7684\u9818\u5c0e\u4eba\uff0c\u6216\u525b\u597d\u6709\u4e00\u000d\u000a\u6279\u71b1\u60c5\u7684\u5e79\u90e8\uff0c\u90a3\u9ebc\u9019\u500b\u5718\u9ad4\u53ef\u80fd\u6703\u98a8\u98a8\u706b\u706b\u5730\u767c\u63ee\u5f71\u97ff\u529b\uff0c\u4f46\u662f\u4e09\u5e74\u3001\u4e94\u5e74\uff0c\u4e43\u81f3\u5341\u4f86\u5e74\u000d\u000a\u5f8c\uff0c\u9019\u500b\u9818\u5c0e\u4eba\u4e0b\u53f0\u6216\u9000\u4f11\uff0c\u5f80\u5f80\u6574\u500b\u7d44\u7e54\u5c31\u6b64\u6c92\u843d\u3002 \u000d\u000a \u82e5\u662f\u5546\u696d\u5718\u9ad4\u56e0\u7af6\u722d\u6216\u6642\u4ee3\u8b8a\u9077\u800c\u6709\u6240\u8b8a\u52d5\uff0c\u90a3\u662f\u5f88\u6b63\u5e38\u7684\uff0c\u4f46\u5047\u5982\u662f\u4f86\u81ea\u5584\u610f\u6350\u6b3e\u7684\u000d\u000a\u516c\u76ca\u5718\u9ad4\uff0c\u56e0\u5e79\u90e8\u7684\u8b8a\u52d5\u800c\u5931\u53bb\u5f71\u97ff\u529b\uff0c\u90a3\u5c31\u662f\u6574\u500b\u793e\u6703\u7684\u640d\u5931\u4e86\u3002 \u000d\u000a \u56e0\u6b64\u4e8c\u5341\u4e03\u5e74\u4f86\uff0c\u8352\u1c00\u4fdd\u8b77\u5354\u6703\u6700\u1bff\u8996\u7684\u5c31\u662f\u5fd7\u5de5\u8a13\u7df4\u4e86\uff0c\u900f\u904e\u5404\u7a2e\u65b9\u6cd5\u8b93\u7368\u7279\u7684\u8352\u1c00\u000d\u000a\u6587\u5316\u80fd\u5920\u4e00\u4ee3\u4e00\u4ee3\u50b3\u627f\u4e0b\u53bb\u3002 \u000d\u000a \u6700\u8fd1\u807d\u4e86\u7ba1\u7406\u5b78\u5927\u5e2b\u67ef\u6797\u65af\u7684\u6f14\u8b1b\uff0c\u9817\u6709\u540c\u611f\u3002\u67ef\u6797\u65af\u9019\u4e00\u8f29\u5b50\u6700\u95dc\u6ce8\u7684\u8b70\u984c\u5c31\u662f:\u70ba\u4ec0\u000d\u000a\u9ebc\u6709\u4e9b\u516c\u53f8\u80fd\u5920\u5982\u6b64\u6046\u4e45\u5353\u8d8a?\u800c\u6709\u4e9b\u537b\u4e0d\u80fd? \u000d\u000a \u4ed6\u5beb\u4e86\u597d\u5e7e\u672c\u66a2\u92b7\u5168\u4e16\u754c\u7684\u7ba1\u7406\u5b78\u7d93\u5178\u8457\u4f5c\uff0c\u5982\u300c\u57fa\u696d\u9577\u9752\u300d\u3001\u300c\u5f9eA\u5230A+\u300d\u2026\u2026\u7b49\u7b49\uff0c\u000d\u000a\u5176\u4e2d\u6709\u4e00\u672c\u8ac7\u975e\u71df\u5229\u7d44\u7e54\u7684\u300c\u5f9eA\u5230A+\u7684\u793e\u6703\u300d\uff0c\u4ed6\u8a8d\u70ba\u53ea\u6709\u5353\u8d8a\u7684\u975e\u71df\u5229\u7d44\u7e54\uff0c\u624d\u80fd\u50ac\u751f\u000d\u000a\u51fa\u5353\u8d8a\u7684\u793e\u6703\uff0c\u591a\u5e74\u524d\u6211\u4e5f\u66fe\u5c31\u9019\u672c\u66f8\u5230\u4e00\u4e9b\u516c\u76ca\u5718\u9ad4\u5c0e\u8b80\u5206\u4eab\u904e\u3002 \u000d\u000a \u5728\u4eca\u5e74\u4e09\u6708\u9019\u5834\u67ef\u6797\u65af\u4f86\u53f0\u7063\u7684\u6f14\u8b1b\uff0c\u5167\u5bb9\u662f\u8b1b\u4ed6\u6700\u8fd1\u51fa\u7684\u66f8\u300c\u6046\u4e45\u5353\u8d8a\u7684\u4fee\u7149\u300d\uff0c\u5206\u000d\u000a\u70ba\u6709\u7d00\u5f8b\u7684\u7d44\u7e54\u6210\u54e1\uff0c\u53ca\u6709\u7d00\u5f8b\u7684\u601d\u8003\uff0c\u6700\u5f8c\u662f\u6709\u7d00\u5f8b\u7684\u884c\u52d5\u3002 \u000d\u000a \u6709\u7d00\u5f8b\u7684\u5e79\u90e8\u5fc5\u9808\u662f\u7b2c\u4e94\u7d1a\u9818\u5c0e\u8005\uff0c\u7d50\u5408\u4e86\u8b19\u905c\u8ddf\u5805\u5b9a\u7684\u610f\u5fd7\u3002\u9019\u88e1\u7684\u8b19\u905c\u662f\u6307\uff0c\u5118\u7ba1\u000d\u000a\u6709\u5f37\u70c8\u7684\u4fe1\u5fc3\u8207\u610f\u5fd7\uff0c\u4f46\u662f\u9019\u4e9b\u4fe1\u5fc3\u7121\u95dc\u65bc\u500b\u4eba(\u4e0d\u662f\u82f1\u96c4\u4e3b\u7fa9\u5f0f\u7684\u9818\u5c0e\u8005)\uff0c\u800c\u662f\u4ee5\u5275\u9020\u5049\u000d\u000a\u5927\u7d44\u7e54\u70ba\u552f\u4e00\u8003\u1c01\u3002 "," \u53e6\u5916\u4e00\u9ede\u4e5f\u662f\u6211\u4e8c\u5341\u591a\u5e74\u4f86\u770b\u4ed6\u7684\u66f8\uff0c\u5370\u8c61\u6700\u6df1\uff0c\u4e5f\u6df1\u6df1\u8a8d\u540c\u7684\u2015\u2015\u627e\u5c0d\u7684\u4eba\u4e0a\u8eca\uff0c\u4e26\u000d\u000a\u4e14\u628a\u6b63\u78ba\u7684\u4eba\u5b89\u6392\u5728\u6b63\u78ba\u7684\u4f4d\u7f6e\u4e0a\uff0c\u751a\u81f3\u4e0d\u9700\u8981\u77e5\u9053\u9019\u8eca\u5b50\u8981\u958b\u53bb\u54ea\u88e1\uff0c\u56e0\u70ba\u5c0d\u7684\u4eba\u6703\u60f3\u51fa\u000d\u000a\u5982\u4f55\u628a\u5b83\u958b\u5230\u4e00\u500b\u5f88\u68d2\u7684\u5730\u65b9\u3002\u7684\u78ba\uff0c\u5728\u975e\u71df\u5229\u7d44\u7e54\uff0c\u627e\u5c0d\u7684\u4eba\u6b63\u662f\u1bff\u4e2d\u4e4b\u1bff\uff0c\u53ea\u8981\u4eba\u5c0d\uff0c\u000d\u000a\u4f60\u5c31\u653e\u624b\u8b93\u4ed6\u53bb\u767c\u63ee\uff0c\u9019\u8ddf\u50b3\u7d71\u4f01\u7ba1\u7684\u6982\u5ff5\u662f\u76f8\u53cd\u7684\uff0c\u4e00\u822c\u7d44\u7e54\u662f\u6211\u5011\u60f3\u505a\u4ec0\u9ebc\u4e8b\uff0c\u7136\u5f8c\u627e\u000d\u000a\u9069\u5408\u7684\u4eba\u4f86\u505a(\u4e00\u500b\u863f\u8514\u4e00\u500b\u5751)\uff0c\u4f46\u662f\u67ef\u6797\u65af\u8ac7\u7684\u662f\u300c\u56e0\u4eba\u8a2d\u4e8b\u300d\uff0c\u53ea\u8981\u4ed6\u662f\u5c0d\u7684\u4eba\uff0c\u5c31\u8b93\u000d\u000a\u4ed6\u505a\u4ed6\u60f3\u505a\u7684\u4e8b\uff0c\u4e5f\u5c31\u662f\u300c\u6709\u9019\u500b\u4eba\uff0c\u624d\u7522\u751f\u9019\u4e9b\u4e8b\u300d\u3002 \u000d\u000a \u7b2c\u4e8c\u968e\u6bb5\u6240\u8b02\u6709\u7d00\u5f8b\u7684\u601d\u8003\uff0c\u5305\u62ec\u4e86\u4e09\u500b\u539f\u5247\u3002 \u000d\u000a \u9996\u5148\u662f\u64c1\u62b1\u517c\u5bb9\u4e26\u84c4\u3002\u4ed6\u5011\u4e0d\u6703\u7528\u300c\u6216\u300d(or) \u4f86\u81ea\u6211\u8a2d\u9650\uff0c\u800c\u662f\u517c\u9867\u300c\u8207\u300d(and)\u7684\u65b9\u6cd5\uff0c\u000d\u000a\u4e5f\u5c31\u662f\u60f3\u8fa6\u6cd5\u517c\u9867\u4f3c\u4e4e\u5169\u500b\u76f8\u53cd\u65b9\u5411\u7684\u76ee\u6a19\uff0c\u9019\u4e5f\u662f\u6211\u5411\u4f86\u7684\u884c\u4e8b\u6e96\u5247:\u300c\u722d\u4e00\u6642\u4e5f\u8981\u722d\u5343\u000d\u000a\u79cb\u300d\u3002 \u000d\u000a \u7b2c\u4e8c\u500b\u539f\u5247\u662f\u9762\u5c0d\u6b98\u9177\u7684\u73fe\u5be6\uff0c\u4e5f\u5c31\u662f\u8981\u6709\u5371\u6a5f\u610f\u8b58\uff0c\u7b2c\u4e09\u500b\u539f\u5247\u662f\u4ed6\u5728\u904e\u53bb\u5e7e\u672c\u66f8\u5f37\u000d\u000a\u8abf\u7684\u523a\u875f\u539f\u5247\u2015\u2015\u91d0\u6e05\u4f60\u6700\u71b1\u611b\u4ec0\u9ebc?\u4f60\u5728\u54ea\u65b9\u9762\u6700\u9802\u5c16?\u4ee5\u53ca\u4f60\u7684\u7d93\u6fdf\u5f15\u64ce\u5982\u4f55\u9a45\u52d5?\u7136\u5f8c\u000d\u000a\u6839\u64da\u9019\u4e9b\u601d\u8003\u4f86\u5236\u5b9a\u4e00\u7cfb\u5217\u7684\u6c7a\u7b56\u3002 \u000d\u000a \u6700\u5f8c\u4e00\u500b\u968e\u6bb5\u662f\u6709\u7d00\u5f8b\u7684\u884c\u52d5\u3002 \u000d\u000a \u67ef\u6797\u65af\u5728\u904e\u53bb\u7684\u66f8\u88e1\u4e5f\u63d0\u51fa\u300c\u98db\u8f2a\u300d\u7406\u8ad6\uff0c\u6211\u5011\u5fc5\u9808\u7528\u76e1\u8a31\u591a\u65b9\u6cd5\u4e0d\u65b7\u7d2f\u7a4d\u52d5\u80fd\u624d\u80fd\u555f\u000d\u000a\u52d5\u98db\u8f2a\uff0c\u4f46\u4e00\u65e6\u63a8\u52d5\uff0c\u98db\u8f2a\u5c31\u6703\u6709\u500b\u529b\u1c01\u5f80\u524d\u884c\uff0c\u56e0\u6b64\u6709\u7d00\u5f8b\u7684\u884c\u52d5\u7b2c\u4e00\u6b65\u5c31\u662f\u4f86\u81ea\u826f\u597d\u7684\u000d\u000a\u6c7a\u7b56\u52a0\u4e0a\u826f\u597d\u57f7\u884c\u529b\u6240\u555f\u52d5\uff1b\u7b2c\u4e8c\u500b\u539f\u5247\u662f\u300c\u4e8c\u5341\u54e9\u884c\u8ecd\u300d\uff0c\u60f3\u50cf\u8981\u7a7f\u8d8a\u6c99\u6f20\uff0c\u6211\u5011\u6709\u5169\u7a2e\u000d\u000a\u65b9\u6cd5\uff0c\u4e00\u7a2e\u662f\u5728\u597d\u5929\u6c23\u6642\u4e00\u5929\u8dd1\u4e94\u5341\u54e9\uff0c\u5929\u6c23\u4e0d\u597d\u5c31\u505c\u4e0b\u4f86\u4f11\u606f\uff1b\u53e6\u4e00\u7a2e\u65b9\u6cd5\u662f\u7121\u8ad6\u5929\u6c23\u597d\u000d\u000a\u58de\uff0c\u6bcf\u5929\u90fd\u884c\u8ecd\u4e8c\u5341\u54e9\u3002 \u000d\u000a \u4ed6\u4e3b\u5f35\u8981\u7a69\u5b9a\uff0c\u7121\u8ad6\u6709\u591a\u5c11\u4e8b\u60c5\u4e0d\u5728\u6211\u5011\u63a7\u5236\u7bc4\u570d\uff0c\u4f46\u662f\u9084\u662f\u8981\u60f3\u8fa6\u6cd5\u505a\u5230\u4e00\u6a23\u7684\u7a69\u000d\u000a\u5b9a\uff0c\u56e0\u6b64\u5e73\u5e38\u5c31\u8981\u8981\u6c42\u5728\u7121\u5e8f\u4e2d\u52a0\u5f37\u79e9\u5e8f\uff0c\u5728\u6df7\u4e82\u4e2d\u5f37\u52a0\u7d00\u5f8b\u3002 \u000d\u000a \u6700\u5f8c\uff0c\u67ef\u6797\u65af\u4e3b\u5f35\uff0c\u505a\u5230\u7a69\u5b9a\u4e4b\u5f8c\uff0c\u9084\u8981\u601d\u8003\u5982\u4f55\u505a\u5230\u5275\u65b0\uff0c\u4ed6\u5efa\u8b70\uff0c\u5148\u5c04\u5b50\u5f48\uff0c\u518d\u5c04\u000d\u000a\u7832\u5f48\uff0c\u4e5f\u5c31\u662f\u5148\u5c0f\u898f\u6a21\u9a57\u8b49\u5275\u65b0\u6982\u5ff5\uff0c\u518d\u64f4\u5927\u898f\u6a21\u3002 \u000d\u000a \u67ef\u6797\u65af\u9577\u671f\u7814\u7a76\u5168\u4e16\u754c\u9802\u5c16\u7684\u516c\u53f8\uff0c\u767c\u73fe\u5e7e\u5341\u5e74\u524d\u898f\u6a21\u6392\u5165\u5168\u4e16\u754c\u4e94\u767e\u5927\u7684\u4f01\u696d\u4e2d\uff0c\u53ea\u000d\u000a\u6709\u767e\u5206\u4e4b\u5341\u4e94\u81f3\u4eca\u4ecd\u5b58\u5728\uff0c\u63db\u53e5\u8a71\u8aaa\uff0c\u5373\u4f7f\u4e94\u3001\u516d\u5341\u5e74\u524d\u898f\u6a21\u5927\u5230\u5f88\u96e3\u60f3\u50cf\u7684\u4f01\u696d\uff0c\u537b\u6709\u767e\u000d\u000a\u5206\u4e4b\u516b\u5341\u4e94\u73fe\u5728\u5df2\u6d88\u5931\u3002 \u000d\u000a \u4ed6\u7814\u7a76\u9019\u4e9b\u6b9e\u843d\u7684\u7d44\u7e54\u767c\u73fe\u4e3b\u8981\u6709\u5e7e\u500b\u539f\u56e0\uff0c\u9996\u5148\u662f\u4ed6\u5011\u55aa\u5931\u4e86\u5275\u7acb\u521d\u671f\u70ba\u4e86\u751f\u5b58\u800c\u4e0d\u000d\u000a\u65b7\u4fdd\u6301\u8b66\u6212\u7684\u72c0\u614b\uff0c\u4e5f\u5c31\u662f\u8b8a\u5f97\u81ea\u5927\u53c8\u5b98\u50da\u3002 "," \u518d\u4f86\u662f\u4f01\u696d\u5fc5\u9808\u5efa\u7acb\u8d77\u6b63\u78ba\u7684\u6a5f\u5236\uff0c\u8b93\u4f01\u696d\u7684\u6210\u529f\u4e0d\u662f\u56e0\u70ba\u67d0\u4f4d\u9818\u5c0e\u8005\u6216\u67d0\u500b\u60f3\u6cd5\uff0c\u67ef\u000d\u000a\u6797\u65af\u7528\u4e00\u500b\u6709\u8da3\u7684\u6bd4\u55bb\u300c\u591a\u9020\u9418\uff0c\u5c11\u5831\u6642\u300d\u3002 \u000d\u000a \u6700\u5f8c\u662f\u4fdd\u6301\u6838\u5fc3\uff0c\u4f46\u8981\u6301\u7e8c\u53d7\u523a\u6fc0\u800c\u9032\u6b65\uff1b\u4e5f\u5c31\u4fdd\u6301\u4e00\u5957\u6c38\u6046\u7684\u6838\u5fc3\u50f9\u503c\u89c0\u548c\u5b97\u65e8\uff0c\u4f46\u000d\u000a\u540c\u6642\uff0c\u4e5f\u8981\u64c1\u6709\u4e0d\u65b7\u9032\u6b65\u7684\u52d5\u529b\u2015\u2015\u6539\u8b8a\u3001\u6539\u9032\u3001\u5275\u65b0\u548c\u66f4\u65b0\u3002 \u000d\u000a \u7d44\u7e54\u8981\u57fa\u696d\u9577\u9752\uff0c\u5728\u7d93\u6b77\u591a\u6b21\u4e16\u4ee3\u4ea4\u66ff\u5f8c\uff0c\u4ecd\u7136\u84ec\u52c3\u767c\u5c55\uff0c\u7d55\u4e0d\u662f\u55ae\u55ae\u9760\u4e00\u4f4d\u5049\u5927\u7684\u9818\u000d\u000a\u5c0e\u4eba\uff0c\u5049\u5927\u7684\u5275\u610f\u6216\u8a08\u5283\uff0c\u800c\u662f\u4f86\u81ea\u65bc\u7d44\u7e54\u88e1\u7684\u6240\u6709\u4eba\u90fd\u5171\u540c\u8a8d\u53ef\u7684\u6838\u5fc3\u50f9\u503c\u8207\u53ef\u4ee5\u6fc0\u52f5\u6211\u000d\u000a\u5011\u4e0d\u65b7\u52aa\u529b\u7684\u5922\u60f3\uff0c\u540c\u6642\u4e5f\u80fd\u5920\u56de\u61c9\u6642\u4ee3\u8b8a\u9077\uff0c\u9858\u610f\u7121\u6b62\u5883\u5730\u8ffd\u6c42\u5275\u65b0\u8207\u9032\u6b65\u3002 ","\uff14\uff0f\uff18 \u000d\u000a\u751f\u614b\u4fdd\u80b2\u662f\u5834\u7121\u9650\u8cfd\u5c40\u7684\u904b\u52d5\u2015\u8352\u1c00\u9818\u822a\u54e1\u57f9\u8a13\u71df\u884c\u524d\u611f\u60f3\u4e4b\u4e00 110.04.13 \u000d\u000a \u8a18\u5f97\u591a\u5e74\u4f86\u8ddf\u8352\u1c00\u7684\u4f19\u4f34\u8ac7\u8352\u1c00\u7684\u7b56\u7565\u8207\u6587\u5316\u6642\uff0c\u5076\u723e\u6703\u63d0\u5230\uff0c\u4e0d\u7ba1\u6211\u5011\u591a\u9ebc\u52aa\u529b\uff0c\u74b0\u000d\u000a\u5883\u4fdd\u8b77\u662f\u6c38\u9060\u7121\u6cd5\u6210\u529f\u4e5f\u7121\u6cd5\u9054\u6210\u7684\u76ee\u6a19\uff0c\u63db\u53e5\u8a71\u8aaa\uff0c\u751f\u614b\u4fdd\u80b2\u7d42\u7a76\u662f\u689d\u6f2b\u9577\u7121\u6b62\u76e1\u6c38\u9060\u7121\u000d\u000a\u6cd5\u62b5\u9054\u7d42\u9ede\u7684\u52aa\u529b\u3002 \u000d\u000a \u4e5f\u56e0\u70ba\u6211\u5011\u6709\u5982\u6b64\u7684\u9ad4\u8a8d\uff0c\u6240\u4ee5\u8352\u1c00\u624d\u505a\u51fa\u4e0d\u540c\u7684\u9078\u64c7\uff0c\u63a1\u53d6\u4e0d\u540c\u7684\u7b56\u7565\uff0c\u5f62\u6210\u73fe\u5728\u7684\u000d\u000a\u6587\u5316\u3002 \u000d\u000a \u5f80\u5f80\u9650\u65bc\u6642\u9593\u7684\u95dc\u4fc2\uff0c\u8aaa\u9019\u4e9b\u8a71\u6642\u6211\u901a\u5e38\u6c92\u6709\u9032\u4e00\u6b65\u7684\u89e3\u91cb\uff0c\u4e5f\u4e0d\u77e5\u9053\u4f19\u4f34\u5011\u662f\u5426\u80fd\u6709\u000d\u000a\u8ddf\u6211\u4e00\u6a23\u7684\u9ad4\u8a8d\u3002\u6700\u8fd1\u770b\u4e86\u8cfd\u9580\u897f\u5948\u514b\u6240\u5beb\u7684\u300c\u7121\u9650\u8cfd\u5c40\u300d\u9019\u672c\u66f8\uff0c\u60f3\u5230\u525b\u597d\u53ef\u4ee5\u7528\u4f01\u7ba1\u9818\u000d\u000a\u57df\u7684\u8cfd\u5c40\u7406\u8ad6\u4f86\u8ac7\u8ac7\u9019\u500b\u89c0\u9ede\u3002 \u000d\u000a \u53ea\u8981\u6709\u81f3\u5c11\u4e8c\u4f4d\u73a9\u5bb6\uff0c\u8cfd\u5c40\u5c31\u80fd\u6210\u7acb\uff0c\u8cfd\u5c40\u5206\u70ba\u6709\u9650\u8cfd\u5c40\u8207\u7121\u9650\u8cfd\u5c40\u3002\u6709\u9650\u8cfd\u5c40\u4e2d\uff0c\u6709\u000d\u000a\u65e2\u5b9a\uff0c\u5df2\u77e5\u7684\u73a9\u5bb6\uff0c\u4ee5\u53ca\u56fa\u5b9a\u7684\u898f\u5247\uff0c\u5927\u5bb6\u4e8b\u524d\u6709\u5171\u8b58\uff0c\u9054\u5230\u67d0\u500b\u76ee\u6a19\u5f8c\uff0c\u8cfd\u5c40\u5c31\u7d50\u675f\u3002 \u000d\u000a \u76f8\u53cd\u7684\uff0c\u7121\u9650\u8cfd\u5c40\u7684\u73a9\u5bb6\u6709\u4e9b\u5df2\u77e5\uff0c\u6709\u4e9b\u672a\u77e5\uff0c\u6c92\u6709\u660e\u78ba\u6216\u4e8b\u5148\u540c\u610f\u7684\u898f\u5247\uff0c\u66f4\u1bff\u8981\u7684\u000d\u000a\u662f\uff0c\u7121\u9650\u8cfd\u5c40\u6c92\u6709\u6642\u9593\u9650\u5236\uff0c\u56e0\u70ba\u6c92\u6709\u7d42\u9ede\u7dda\uff0c\u6c92\u6709\u771f\u6b63\u7684\u7d50\u675f\uff0c\u6240\u4ee5\u4e5f\u4e0d\u6703\u6709\u4eba\u771f\u6b63\u300c\u8d0f\u000d\u000a\u5f97\u300d\u4e00\u5834\u7121\u9650\u8cfd\u5c40\u3002\u7121\u9650\u8cfd\u5c40\u7684\u6838\u5fc3\u7279\u5fb5\u662f\u4e0d\u505c\u5730\u73a9\u4e0b\u53bb\uff0c\u8b93\u8cfd\u5c40\u6301\u7e8c\u4e0b\u53bb\u3002 \u000d\u000a \u5728\u6709\u9650\u8cfd\u5c40\u4e2d\uff0c\u6642\u9593\u4e00\u5230\u8cfd\u5c40\u5c31\u7d50\u675f\uff0c\u7522\u751f\u8d0f\u5bb6\u8ddf\u8f38\u5bb6\uff1b\u7121\u9650\u8cfd\u5c40\u4e0d\u540c\uff0c\u8cfd\u5c40\u672c\u8eab\u6703\u6301\u000d\u000a\u7e8c\uff0c\u4f46\u73a9\u5bb6\u7684\u6642\u9593\u6703\u7528\u5b8c\uff0c\u800c\u4e14\u7121\u9650\u8cfd\u5c40\u6c92\u6709\u6240\u8b02\u8f38\u8d0f\uff0c\u73a9\u5bb6\u4e00\u65e6\u8017\u76e1\u8cc7\u6e90\uff0c\u6216\u55aa\u5931\u73a9\u4e0b\u53bb\u000d\u000a\u7684\u610f\u5fd7\uff0c\u5c31\u6703\u9000\u51fa\u8cfd\u5c40\u3002\u56e0\u6b64\uff0c\u9762\u5c0d\u7121\u9650\u8cfd\u5c40\uff0c\u5c31\u4e0d\u8a72\u4ee5\u8ab0\u6700\u53b2\u5bb3\uff0c\u8ab0\u662f\u8d0f\u5bb6\u4f86\u601d\u8003\uff0c\u800c\u662f\u000d\u000a\u8981\u8457\u773c\u5728\u5efa\u7acb\u5f37\u5065\u7684\u7d44\u7e54\uff0c\u80fd\u5920\u5ef6\u7e8c\u5230\u672a\u4f86\u7684\u4e16\u4ee3\u3002 \u000d\u000a \u751f\u614b\u4fdd\u80b2\u7684\u52aa\u529b\u662f\u7121\u9650\u8cfd\u5c40\uff0c\u7576\u4e0b\u8352\u1c00\u7684\u9818\u822a\u54e1\u518d\u52aa\u529b\u518d\u5929\u7e31\u82f1\u660e\uff0c\u4e5f\u6c92\u6709\u8fa6\u6cd5\u8aaa\uff0c\u6211\u000d\u000a\u5011\u4fdd\u8b77\u4e0b\u591a\u5c11\u68ee\u6797\u6cb3\u6d41\u3001\u5236\u5b9a\u591a\u5c11\u6cd5\u5f8b\uff0c\u5f9e\u6b64\u5c31\u842c\u4e16\u592a\u5e73\u4e86\u3002\u56e0\u70ba\u65b0\u7684\u4e16\u4ee3\u4e0d\u65b7\u51fa\u751f\uff0c\u74b0\u5883\u000d\u000a\u4e0d\u65b7\u8b8a\u52d5\uff0c\u6bcf\u500b\u4e16\u4ee3\u90fd\u5fc5\u9808\u627e\u5230\u4eba\u8ddf\u81ea\u7136\uff0c\u4eba\u8ddf\u74b0\u5883\u76f8\u8655\u7684\u65b9\u5f0f\uff0c\u6bcf\u500b\u4e16\u4ee3\u4e5f\u90fd\u6703\u906d\u9047\u7368\u7279\u000d\u000a\u7684\u74b0\u5883\u554f\u984c\u5fc5\u9808\u89e3\u6c7a\uff0c\u56e0\u6b64\u53ea\u8981\u9084\u6709\u4eba\u985e\u5b58\u6d3b\u5728\u5730\u7403\u4e0a\uff0c\u9019\u500b\u8cfd\u5c40\u5c31\u4e0d\u6703\u7d50\u675f\u3002 \u000d\u000a \u6709\u9650\u601d\u60df\u7684\u9818\u822a\u54e1\u6703\u7528\u7576\u4e0b\u7d44\u7e54\u7684\u4e00\u5207\u6210\u679c\u4f86\u8b49\u660e\u81ea\u5df1\u591a\u53b2\u5bb3\uff0c\u7a81\u986f\u81ea\u5df1\u7684\u50f9\u503c\uff0c\u4f46\u662f\u000d\u000a\u7121\u9650\u601d\u60df\u7684\u9818\u822a\u54e1\u5247\u662f\u4ed8\u51fa\u81ea\u5df1\u7684\u4e00\u5207\u4f86\u589e\u52a0\u7d44\u7e54\u7684\u50f9\u503c\u8207\u78ba\u4fdd\u7d44\u7e54\u9577\u671f\u5b58\u5728\uff0c\u5982\u6b64\u624d\u80fd\u5e36\u000d\u000a\u7d66\u672a\u4f86\u4e16\u4ee3\u6700\u5927\u7684\u798f\u7949\u3002 "," \u4f5c\u8005\u63d0\u9192\u6709\u9650\u8cfd\u5c40\u5f88\u8a98\u4eba\uff0c\u4ee4\u4eba\u8208\u596e\uff0c\u4e5f\u6703\u4ee4\u4eba\u4e0a\u766e\uff0c\u5c31\u50cf\u8ced\u535a\uff0c\u6bcf\u4e00\u6b21\u52dd\u5229\uff0c\u6bcf\u4e00\u6b21\u000d\u000a\u9032\u7403\uff0c\u5927\u8166\u90fd\u6703\u91cb\u653e\u591a\u5df4\u80fa\uff0c\u9f13\u52f5\u6211\u5011\u1bff\u8907\u540c\u6a23\u7684\u884c\u70ba\u6a21\u5f0f\uff0c\u5c08\u6ce8\u5728\u6709\u9650\u7684\u76ee\u6a19\u6bd4\u5c08\u6ce8\u65bc\u7121\u000d\u000a\u9650\u672a\u4f86\u7684\u9858\u666f\u8981\u5bb9\u6613\u591a\u4e86\uff0c\u4e5f\u66f4\u5bb9\u6613\u7372\u5f97\u638c\u8072\u4ee5\u53ca\u6709\u5f62\u6216\u7121\u5f62\u7684\u734e\u8cde\u3002 \u000d\u000a \u63a1\u53d6\u6709\u9650\u601d\u60df\u6216\u7121\u9650\u601d\u60df\u6703\u8b93\u7d44\u7e54\u8207\u6211\u5011\u500b\u4eba\u8e0f\u4e0a\u5b8c\u5168\u4e0d\u540c\u7684\u65c5\u7a0b\u3002\u63a1\u53d6\u6709\u9650\u601d\u60df\u7684\u65c5\u000d\u000a\u7a0b\uff0c\u53ef\u80fd\u6703\u8d0f\u53ef\u80fd\u6703\u8f38\uff0c\u4f46\u662f\u7576\u9818\u822a\u54e1\u96e2\u53bb\uff0c\u7d44\u7e54\u662f\u5426\u9084\u80fd\u5065\u5168\u4e14\u6709\u5f71\u97ff\u529b\u7684\u5b58\u5728?\u7372\u52dd\u7684\u000d\u000a\u5feb\u6a02\u5f88\u77ed\u66ab\uff0c\u4f46\u662f\u7121\u9650\u601d\u60df\u7684\u9818\u822a\u54e1\u662f\u70ba\u4e86\u4fe1\u5ff5\uff0c\u70ba\u4e86\u6bd4\u81ea\u5df1\u66f4\u9060\u5927\u7684\u7406\u60f3\u800c\u52aa\u529b\uff0c\u6211\u5011\u77e5\u000d\u000a\u9053\u4e0d\u7ba1\u8f38\u8d0f\uff0c\u7121\u9650\u8cfd\u5c40\u4e00\u76f4\u5728\u9032\u884c\u4e2d\uff0c\u6211\u5011\u4ed8\u51fa\u7684\u6bcf\u4efd\u5fc3\u529b\u6ef4\u4e0b\u7684\u6bcf\u6ef4\u6c57\u6c34\uff0c\u90fd\u5c07\u6703\u5e36\u9818\u7d44\u000d\u000a\u7e54\u5f80\u524d\u9081\u9032\u4e00\u9ede\u9ede\uff0c\u800c\u6bcf\u500b\u9032\u5c55\u4e5f\u6703\u8b93\u6211\u5011\u66f4\u6e05\u695a\u672a\u4f86\u7684\u53ef\u80fd\u6027\uff0c\u800c\u6fc0\u52f5\u6211\u5011\u4e0d\u65b7\u524d\u9032\u3002 \u000d\u000a \u4f5c\u8005\u63d0\u5230\uff0c\u60f3\u8981\u4ee5\u7121\u9650\u601d\u60df\u9818\u5c0e\uff0c\u8981\u5177\u5099\u4e94\u9805\u689d\u4ef6\uff0c\u5206\u5225\u662f: \u000d\u000a (\u4e00)\u63a8\u52d5\u4e00\u500b\u5d07\u9ad8\u7684\u4fe1\u5ff5\u3002 \u000d\u000a (\u4e8c)\u5efa\u7acb\u4fe1\u4efb\u7684\u5718\u968a\u3002 \u000d\u000a (\u4e09)\u7814\u7a76\u53ef\u656c\u7684\u5c0d\u624b\u3002 \u000d\u000a (\u56db)\u6e96\u5099\u597d\u6538\u95dc\u5b58\u4ea1\u7684\u61c9\u8b8a\u3002 \u000d\u000a (\u4e94)\u5c55\u73fe\u9818\u5c0e\u8005\u7684\u52c7\u6c23\u3002 \u000d\u000a \u6bcf\u9805\u689d\u4ef6\u4f5c\u8005\u90fd\u8209\u4e86\u4e0d\u5c11\u4f8b\u5b50\u8a73\u7d30\u8aaa\u660e\uff0c\u5728\u6b64\u5c31\u4e0d\u8d05\u8ff0\uff0c\u6709\u8208\u8da3\u5927\u5bb6\u53ef\u4ee5\u81ea\u5df1\u627e\u66f8\u4f86\u000d\u000a\u770b\u3002 \u000d\u000a \u8352\u1c00\u9818\u822a\u54e1\u662f\u9078\u64c7\u7121\u9650\u8cfd\u5c40\u601d\u60df\u7684\u9818\u5c0e\u8005\uff0c\u9019\u8868\u793a\u6211\u5011\u6703\u70ba\u4e86\u6bd4\u81ea\u5df1\u66f4\u5049\u5927\u7684\u4fe1\u5ff5\u800c\u52aa\u000d\u000a\u529b\uff0c\u6211\u5011\u6703\u628a\u64c1\u6709\u76f8\u540c\u9858\u666f\u7684\u4eba\u8996\u70ba\u4f19\u4f34\uff0c\u5efa\u7acb\u4e92\u4fe1\u95dc\u4fc2\uff0c\u4e00\u8d77\u63a8\u52d5\u5171\u540c\u7684\u4fe1\u5ff5\u3002 "]